public void enableIndiaControls_IN(boolean _isIndia = true)
{
    isIndia = _isIndia;
    TabTaxInformation_IN.visible(_isIndia);
    Receipts_IN.visible(_isIndia);
    RetailStoreTable_ds.object(fieldNum(RetailStoreTable, TaxIdentificationNumber)).visible(!_isIndia);
    RetailChannelTable_IN_InterStateTaxGroup.visible(_isIndia);
    RetailChannelTable_IN_IsApplyInterStateTax.visible(_isIndia);

    if (_isIndia)
    {
        RetailChannelTable_IN_DS.query().dataSourceTable(tableNum(RetailChannelTable_IN)).enabled(true);
        RetailStoreTable_IN_DS.query().dataSourceTable(tableNum(RetailStoreTable_IN)).enabled(true);
        TaxInformation_IN_DS.query().dataSourceTable(tableNum(TaxInformation_IN)).enabled(true);

        TaxInformation_IN_ds.linkActive();
        RetailStoreTable_IN_ds.linkActive();
        RetailChannelTable_IN_ds.linkActive();

        retailStoreTable_IN_ds.object(fieldNum(RetailStoreTable_IN, ShowTaxOnTax)).allowEdit(
            retailStoreTable_IN.TaxDetailsType == ReceiptTaxDetailsType_IN::PerTaxComponent);
        RetailChannelTable_IN_ds.object(fieldNum(RetailChannelTable_IN, InterStateTaxGroup)).allowEdit(
            RetailChannelTable_IN.IsApplyInterStateTax == NoYes::Yes);
    }
    else
    {
        RetailChannelTable_IN_DS.query().dataSourceTable(tableNum(RetailChannelTable_IN)).enabled(false);
        RetailStoreTable_IN_DS.query().dataSourceTable(tableNum(RetailStoreTable_IN)).enabled(false);
        TaxInformation_IN_DS.query().dataSourceTable(tableNum(TaxInformation_IN)).enabled(false);
    }
}

