container getSelectedAddressBookNames(SysLookupMultiSelectCtrl addressBookCtrl)
{
    DirAddressBook addressBook;
    container addressBookIds, addressBookNames;
    int i;

    // Fetch using Ids instead of names as the current control getSelectedFieldValues function does not support fetching names with preceding zeros e.g. '0478'
    addressBookIds = addressBookCtrl.get();
    for (i = 1; i <= conlen(addressBookIds); i++)
    {
        select firstonly Name from addressBook where addressBook.RecId == conpeek(addressBookIds, i);

        if (addressBook.recId != 0)
        {
            addressBookNames += addressBook.Name;
        }
    }

    return addressBookNames;
}

