private void updateRetailAddressBook(RetailAddressBookType addressBookType)
{
    // Only do this if we have a retailStoreTable and its address books were loaded
    if (retailStoreTable.RecId && retailStoreTable.RecId == addressBookControlsStoreRecId)
    {
        // Gettting the address from the customer addressbook container
        container addressBookContainer = this.getSelectedAddressBookNames(addressBookType == RetailAddressBookType::Customer? customerAddressBookCtrl: employeeAddressBookCtrl);
        Set selectedAddresses = new Set(Types::String);
        for (int i = 1 ; i <= conLen(addressBookContainer) ; i++)
        {
            selectedAddresses.add(conPeek(addressBookContainer,i));
        }
        // Update the addressbooks to the database
        RetailStoreAddressBook::updateAddressBooks(retailStoreTable.RecId, selectedAddresses, addressBookType);
    }
}

