private void addMergedSelfServicePackageReferenceRange(QueryBuildDataSource dataSource, PackageType packageType)
{
    RetailSelfServiceDefaultPackages defaultPackage = RetailSelfServiceDefaultPackages::findDefaultPackage(
             tableNum(RetailStoreTable), packageType, RetailStoreTable.RecId);

    dataSource.addRange(fieldNum(RetailSelfServiceDefaultPackages, MergedSelfServicePackageReference)).value(int642Str(defaultPackage.MergedSelfServicePackageReference));
}

private void modifyMergedSelfServicePackageRange(QueryBuildDataSource dataSource, PackageType packageType)
{
    RetailSelfServiceDefaultPackages defaultPackage;
    RefRecId storeRecId = RetailStoreTable.RecId;

    if (storeRecId > 0)
    {
        // User is editing a store that was created, get the default package based on this store record identifier.
        defaultPackage = RetailSelfServiceDefaultPackages::findDefaultPackage(
            tableNum(RetailStoreTable), packageType, storeRecId);
    }
    else
    {
        // A new store is being created, get the default package from company.
        defaultPackage = RetailSelfServiceDefaultPackages::findDefaultPackage(
            tableNum(CompanyInfo), packageType, CompanyInfo::current());
    }

    dataSource.findRange(fieldNum(RetailSelfServiceDefaultPackages, MergedSelfServicePackageReference))
        .value(int642Str(defaultPackage.MergedSelfServicePackageReference));
}

/// <summary>
/// Display price report parameter slider dialog to prepare for price report.
/// </summary>
private void runPriceReport()
{
    Args args = new Args(formstr(RetailPriceListParameterSlider));

    RetailPriceListParameters params = new RetailPriceListParameters("");

    params.parmOperatingUnitNumber(omOperatingUnit.OMOperatingUnitNumber);
    params.parmStoreId(RetailStoreTable.StoreNumber);
    args.record(omOperatingUnit);
    args.parmObject(params);

    MenuFunction menuFunction = new MenuFunction(menuitemDisplayStr(RetailPriceListParameterSlider), MenuItemType::Display);

    FormRun formRun = menuFunction.create(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        params = args.parmObject();
    }
}

/// <summary>
/// Validates retail customer and employee address books.
/// </summary>
/// <returns>True if any address book is common between Customer/Employee address book.; otherwise, false.</returns>
private boolean validateRetailAddressBooks()
{
    container customerAddressValues = customerAddressBookCtrl.getSelectedFieldValues();
    container employeeAddressValues = employeeAddressBookCtrl.getSelectedFieldValues();

    for (int i = 1 ; i <= conLen(customerAddressValues) ; i++)
    {
        str currentCustAddressValue = conPeek(customerAddressValues, i);
        // Check if any address book is common between Customer/Employee address book.
        if (conFind(employeeAddressValues, currentCustAddressValue) != 0)
        {
            return true;
        }
    }
    return false;
}

public void init()
{
    HcmWorker               hcmWorker;
    QueryBuildDataSource    qbds, storeAddresssBookDS, dirAddressBookPartyDS;

    super();

    RetailStoreTable_ds.object(fieldNum(RetailStoreTable,DefaultCustAccount)).mandatory(true);
    RetailStoreTable_ds.object(fieldNum(RetailStoreTable,inventLocation)).mandatory(true);
    retailStoreTable_ds.object(fieldNum(RetailStoreTable, TaxGroup)).mandatory(true);

    if (element.args().dataset() && element.args().dataset() == tablenum(HcmWorker))
    {
        hcmWorker = element.args().record();

        qbds = this.query().dataSourceTable(tableNum(RetailStoreTable));
        storeAddresssBookDS = qbds.addDataSource(tableNum(RetailStoreAddressBook));
        storeAddresssBookDS.relations(true);
        storeAddresssBookDS.addRange(fieldNum(RetailStoreAddressBook, AddressBookType)).value(enum2str(RetailAddressBookType::Employee));
        storeAddresssBookDS.fetchMode(QueryFetchMode::One2One);
        storeAddresssBookDS.joinMode(JoinMode::ExistsJoin);

        dirAddressBookPartyDS = storeAddresssBookDS.addDataSource(tableNum(DirAddressBookParty));
        dirAddressBookPartyDS.addLink(fieldNum(RetailStoreAddressBook, AddressBook), fieldNum(DirAddressBookParty, AddressBook));
        dirAddressBookPartyDS.addRange(fieldNum(DirAddressBookParty, Party)).value(queryValue(hcmWorker.Person));
        dirAddressBookPartyDS.fetchMode(QueryFetchMode::One2One);
    }
}

