public int active()
{
    int ret;
    RefRecId storeDataAreaRecId;

    ret = super();

    if (RetailStoreTable.inventLocationDataAreaId && RetailStoreTable.inventLocationDataAreaId != RetailStoreTable.company())
    {
        storeDataAreaRecId = CompanyInfo::findDataArea(RetailStoreTable.inventLocationDataAreaId).RecId;
        Rounding_RoundingAccountLedgerDimension.parmCurrentLedgerCOA(Ledger::chartOfAccounts(storeDataAreaRecId));

        DimensionEntryControl.parmCompany(RetailStoreTable.inventLocationDataAreaId);
        DimensionEntryControl.reactivate();
    }
    else if (ret)
    {
        Rounding_RoundingAccountLedgerDimension.parmCurrentLedgerCOA(LedgerChartOfAccounts::current());

        DimensionEntryControl.parmCompany(RetailStoreTable.company());
        DimensionEntryControl.reactivate();
    }

    RetailStoreTable_DS.object(fieldnum(RetailStoreTable, closingMethod)).allowEdit(RetailStoreTable.statementMethod != RetailStaffTerminalTotal::Staff);

    element.updateControls();

    element.updateUseCustomerBasedTaxExemption();

    element.hideControlsForRetailTrial();

    // <GIN>
    element.enableIndiaControls_IN(RetailStoreTable.isIndiaCompanyRetailStore_IN());
    // </GIN>

    // Re-execute these data sources since the cursor on grid RetailStoreTable grid.
    RetailSelfServicePackageMPOSDefaults_ds.executeQuery();
    RetailSelfServicePackageMPOSWithOfflineDefaults_ds.executeQuery();

    RetailChannelIdFormControlHelper::setRetailChannelIdControlEditableStatus(RetailStoreTable_RetailChannelId, RetailStoreTable.RecId == 0);

    return ret;
}

void create(boolean append = false, boolean extern = false)  // If created externally
{
    super(append);

    RetailStoreTable.ChannelType = RetailChannelType::RetailStore;
    RetailStoreTable.cultureName = CompanyInfo::languageId();

