    }
}

void delete()
{
    ttsbegin;

    element.numberSeqStoreId().formMethodDataSourceDelete();
    element.numberSeqRetailChannelId().formMethodDataSourceDelete();

    super();

    ttscommit;
}

void write()
{
    boolean isInsert = RetailStoreTable.RecId == 0;
    isWriting = true;
    ttsbegin;

    // Ensure that RetailStoreTable and OMOperatingUnit are updated transactionally.
    if (isInsert)
    {
        oMOperatingUnit.insert();
    }
    else
    {
        oMOperatingUnit.update();
    }

    element.numberSeqStoreId().formMethodDataSourceWrite();
    element.numberSeqRetailChannelId().formMethodDataSourceWrite();

    RetailStoreTable.OMOperatingUnitID = OMOperatingUnit.RecId;
    super();

    if (isInsert)
    {
        if (RetailSelfServicePackageMPOSDefaults.MergedSelfServicePackageReference > 0)
        {
            RetailSelfServicePackageMPOSDefaults.TableReferenceId = RetailStoreTable.RecId;
            RetailSelfServicePackageMPOSDefaults.insert();
        }

        if (RetailSelfServicePackageMPOSWithOfflineDefaults.MergedSelfServicePackageReference > 0)
        {
            RetailSelfServicePackageMPOSWithOfflineDefaults.TableReferenceId = RetailStoreTable.RecId;
            RetailSelfServicePackageMPOSWithOfflineDefaults.insert();
        }
    }

    if (isInsert && addressBookControlsStoreRecId == 0)
    {
        addressBookControlsStoreRecId = RetailStoreTable.RecId;

        // update the addressbooks when saving the data from retailstoretable
        // #Bug 438872 to consolidate both insert/update places into one. Now this place is only handled for insert.
        element.updateAddressBooks();
    }

    // <GIN>
    if (isIndia)
    {
        retailStoreTable_IN.RetailStoreTable = retailStoreTable.RecId;
        retailStoreTable_IN.write();

        // need to force the write in the create case since the form won't do it.
        retailChannelTable_IN.RetailChannelTable = retailStoreTable.RecId;
        retailChannelTable_IN.TaxGroupDataAreaId = retailStoreTable.inventLocationDataAreaId;
        retailChannelTable_IN.write();
    }
    // </GIN>

    if (!RetailChannelTableExt.Channel)
    {
        RetailChannelTableExt.Channel = RetailStoreTable.RecId;
        RetailChannelTableExt.write();
    }

    ttscommit;
    isWriting = false;
}

private boolean isAddressBookSelectionEmpty(SysLookupMultiSelectCtrl addressBookCtrl)
