public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret && !element.numberSeqStoreId().formMethodDataSourceValidateWrite())
    {
        ret = false;
    }

    if (ret && this.isAddressBookSelectionEmpty(customerAddressBookCtrl))
    {
        ret = checkFailed(strFmt("@SYS84753", "@RET5718"));
    }

    if (ret && this.isAddressBookSelectionEmpty(employeeAddressBookCtrl))
    {
        ret = checkFailed(strFmt("@SYS84753", "@RET5719"));
    }

    return ret;
}

