/// <summary>
/// Enable or disable buttons based on selection.
/// </summary>
/// <returns>The return value.</returns>
public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        HardwareConfigurationButton.enabled(retailStoreHardwareStationTable.RecId &&
            retailStoreHardwareStationTable.HardwareProfileId);
    }

    // Table RetailStoreHardwareStationTable has a new field self-service merged reference package.
    // Call the method below in case the new field does not have any value, but the old field does
    // since now we're showing the new field on this UI and make the legacy field invisible.
    // new field (visible): RetailStoreHardwareStationTable.MergedSelfServicePackageReference
    // legacy field (hidden): RetailStoreHardwareStationTable.SelfServicePackage
    RetailSelfServiceMigrationHelper::populateMergedPackageReferenceFromSelfServicePackageReference(
        this.cursor(),
        fieldNum(RetailStoreHardwareStationTable, SelfServicePackage),
        fieldNum(RetailStoreHardwareStationTable, MergedSelfServicePackageReference));

    return ret;
}

/// <summary>
/// Gets or sets the <c>HostName</c> field.
/// </summary>
/// <param name = "_set">True if the value is being set; otherwise, false.</param>
/// <param name = "_retailStoreHardwareStationTable">The current <c>RetailStoreHardwareStationTable</c> record.</param>
/// <param name = "_hostName">The host name being set by the user.</param>
/// <returns>The host name to be displayed.</returns>
/// <remarks>If the hardware station is of type Dedicated, we hide the value being set.</remarks>
public edit RetailHardwareStationHostName editHostName(boolean _set, RetailStoreHardwareStationTable _retailStoreHardwareStationTable, RetailHardwareStationHostName _hostName)
{
    retailStoreHardwareStationTable_HostName.allowEdit(retailStoreHardwareStationTable.HardwareStationType != RetailHardwareStationEnumType::Dedicated);

    if (_retailStoreHardwareStationTable.HardwareStationType == RetailHardwareStationEnumType::Dedicated)
    {
        return '';
    }
    else
    {
        if (_set)
        {
            _retailStoreHardwareStationTable.HostName = _hostName;
        }

        return _retailStoreHardwareStationTable.HostName;
    }
}

/// <summary>
/// Validates <c>RetailStoreHardwareStationTable</c> table fields hostName and hardwareProfileId when hardwareStationType is set as shared.
/// </summary>
/// <returns>True if the HostName and HardwareProfileId of <c>RetailStoreHardwareStationTable</c> table is not empty-when HardwareStationType set as shared; otherwise, false.</returns>
public boolean validateWrite()
{
    boolean ret;

    ret = super();

    // Validate hostName filed for HardwareStationType property as shared.
    if (ret && RetailStoreHardwareStationTable.HardwareStationType == RetailHardwareStationEnumType::Shared)
    {
        if (RetailStoreHardwareStationTable.HostName == '')
        {
            ret = checkFailed(strFmt("@SYS84753", "@Retail:HardwareStationHostName"));
        }

        if (RetailStoreHardwareStationTable.HardwareProfileId == '')
        {
            ret = checkFailed(strFmt("@SYS84753", "@Retail:HardwareProfile"));
        }
    }

    return ret;
}

