/// <summary>
/// Allow to download a hardware station package associated to the selected hardware station profile.
/// </summary>
public void clicked()
{
    super();

    if (RetailStoreHardwareStationTable_DS.anyMarked())
    {
        RetailStoreHardwareStationTable selectedRow = RetailStoreHardwareStationTable_DS.getFirst(1, false);

        if (RetailSelfServiceMergedPackageManager::validateDownloadPackageReference(selectedRow, fieldNum(RetailStoreHardwareStationTable, MergedSelfServicePackageReference)))
        {
            str packageDownloadUrl = selfServicePackageManager.generatePackageDownloadLink(selectedRow.MergedSelfServicePackageReference, PackageType::HardwareStation, true);

            // Opens new tab and navigate to specified url
            RetailSelfServicePackageManager::OpenLinkInNewBrowserTab(packageDownloadUrl, "@RETAIL:SelfServicePkgRetrievalError");
        }
    }
    else
    {
        Global::warning("@WAX2216");
    }
}

/// <summary>
/// Download hardware station setup file.
/// </summary>
public void clicked()
{
    super();

    if (RetailStoreHardwareStationTable_DS.anyMarked())
    {
        RetailStoreHardwareStationTable selectedRow = RetailStoreHardwareStationTable_DS.getFirst(1, false);

        RetailSelfServicePackageManager::DownloadConfigXml(selectedRow.RecId, PackageType::HardwareStation);
    }
    else
    {
        Global::warning("@WAX2216");
    }
}

/// <summary>
/// Handles the change of the HardwareStationType selection.
/// </summary>
/// <returns>The return value.</returns>
public int selectionChange()
{
    int ret;

    ret = super();

    if (ret)
    {
        // restore original value for host name
        if (retailStoreHardwareStationTable.HardwareStationType == retailStoreHardwareStationTable.orig().HardwareStationType)
        {
            retailStoreHardwareStationTable.HostName = retailStoreHardwareStationTable.orig().HostName;
        }
        else
        {
            if (retailStoreHardwareStationTable.HardwareStationType == RetailHardwareStationEnumType::Dedicated)
            {
                retailStoreHardwareStationTable.HostName = 'localhost-' + guid2Str(newGuid());
            }
            else
            {
                retailStoreHardwareStationTable.HostName = '';
            }
        }
    }

    return ret;
}

