[Form]
public class RetailStoreTenderTypeCardTable extends FormRun
{
    RetailStoreTenderTypeTable                  tenderTypeTable;
    RetailOnlineChannelTable                    onlineChannel;

}

//hides the form controls for Online channel.
public void hideFormControls()
{
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, ButtonGroup))).visible(false);
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, PosIs))).visible(false);
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, Posting))).visible(false);
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, Setup_manualAuthorization))).visible(false);
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, Setup_enterFleetInfo))).visible(false);
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, Setup_checkModulus))).visible(false);
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, Setup_processLocally))).visible(false);
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, Setup_cashBackLimit))).visible(false);
    Setup_checkExpiredDate.visible(false);
    Setup_allowManualInput.visible(false);
}

public void init()
{
    TableId callerTableId;
    RetailMCRChannelTable retailMCRChannelTable;
    RetailSharedParameters retailSharedParameters;

    super();

    if (element.args() && element.args().caller() && element.args().dataset())
    {
        callerTableId = element.args().record().TableId;

        if (callerTableId == tablenum(RetailStoreTenderTypeTable))
        {
            tenderTypeTable = element.args().record();

            RetailStoreTenderTypeCardTable_ds.object(fieldNum(RetailStoreTenderTypeCardTable, tenderTypeId)).visible(false);
            RetailStoreTenderTypeCardTable_ds.object(fieldNum(RetailStoreTenderTypeCardTable, cardNumberSwiped)).visible(false);
            RetailStoreTenderTypeCardTable_ds.object(fieldNum(RetailStoreTenderTypeCardTable, sameCardAllowed)).visible(false);
            Setup_ManualAuthorization.visible(false);

            // Hide 'expiry date', 'manual number entry', and 'check mode' if Wallet
            if (tenderTypeTable.function == RetailTenderFunction::Wallet)
            {
                Setup_checkModulus.visible(false);
                Setup_checkExpiredDate.visible(false);
                Setup_allowManualInput.visible(false);
            }
        }

        element.showOrHideGiftCardRequiredOptions(false);

        retailSharedParameters = RetailSharedParameters::find();

        boolean isExternalGiftCard = false;

        if (retailSharedParameters && retailSharedParameters.EnableAdvancedExternalGiftCard)
        {
            isExternalGiftCard = (tenderTypeTable.ConnectorName != '');
        }

        // if caller is SP online channel.
        onlineChannel = RetailOnlineChannelTable::find(tenderTypeTable.Channel);
        if (onlineChannel)
        {
            element.hideFormControls();
            element.showOrHideGiftCardRequiredOptions(isExternalGiftCard);
        }
        else
        {
            retailMCRChannelTable = RetailMCRChannelTable::findRecId(tenderTypeTable.Channel);
            if (retailMCRChannelTable)
            {
                element.mcrHideFormControls();
                element.showOrHideGiftCardRequiredOptions(isExternalGiftCard);
            }
        }
    }

    Setup_PreapprovalDurationDays.visible(RetailNonRecurringTokenFeatureExposure::isEnabled());
    Account_LedgerDimension.parmIsDefaultAccount(true);
}

