/// <summary>
/// Show or hide the gift card required options.
/// </summary>
/// <param name = "showOption">True = show options.</param>
[Hookable(false)]
public void showOrHideGiftCardRequiredOptions(boolean showOption)
{
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, Setup_RequireExpirationDate))).visible(showOption);
    element.control(element.controlId(formControlStr(RetailStoreTenderTypeCardTable, Setup_RequirePin))).visible(showOption);
}

boolean checkUseCustomLookup(int _accountTypeEnumValue, int _secondaryAccountTypeEnumValue)
{
    boolean returnValue;

    switch (_accountTypeEnumValue)
    {
        case RetailLedgerBank::Bank:
            returnValue = true;
            break;
        default:
            returnValue = false;
            break;
    }

    return returnValue;
}

public int active()
{
    int ret;

    element.setupCashBackLimitField();

    if (RetailTenderTypeCardSkipCountingRequiredToggle::instance().isEnabled() && !retailStoreTenderTypeCardTable.countingRequired)
    {
        RetailStoreTenderTypeCardTable_ds.object(fieldNum(RetailStoreTenderTypeCardTable, countingRequired)).enabled(false);
    }
    else
    {
        RetailStoreTenderTypeCardTable_ds.object(fieldNum(RetailStoreTenderTypeCardTable, countingRequired)).enabled(true);
    }

    ret = super();

    return ret;
}

