[Form]
public class RetailTerminalTable extends FormRun
{
    NumberSeqFormHandler    numberSeqTerminalId;
    RecId selectedDataGroupRecId;

    #ISOCountryRegionCodes
    boolean isBrazilEnabled = SysCountryRegionCode::isLegalEntityInCountryRegion([#isoBR]);
    boolean isSelfCheckOutFeatureEnabled = FeatureStateProvider::isFeatureEnabled(RetailSelfCheckoutFeature::instance());

}

public void close()
{
    if (numberSeqTerminalId)
    {
        numberSeqTerminalId.formMethodClose();
    }

    super();
}

NumberSeqFormHandler numberSeqTerminalId()
{
    if (!numberSeqTerminalId)
    {
        numberSeqTerminalId = numberSeqFormHandler::newForm(RetailParameters::numRefTerminalId().NumberSequenceId,
                                                             element,
                                                             RetailTerminalTable_DS,
                                                             fieldNum(RetailTerminalTable, terminalId)
                                                            );
    }

    return numberSeqTerminalId;
}

/// <summary>
/// Hides form controls for retail trial.
/// </summary>
public void hideControlsForRetailTrial()
{
    if (RetailTrialUserManagement::isRetailTrialUser())
    {
        PosIsLayout.visible(false);
    }
}

/// <summary>
/// Update Components for Support Offline Database, create/update offline tables if toggle is on, update visibilities.
/// </summary>
private void updateSupportOfflineDatabaseComponents()
{
    if (RetailTerminalTable.SupportOffline)
    {
        RetailTerminalTable.updateSupportOffline();
        RetailTerminalTable_DS.refresh();
    }

    OfflineDbProfile.visible(RetailTerminalTable.SupportOffline);
}

/// <summary>
/// Disables form controls for SelfCheckout.
/// </summary>
private void updateControlsForSelfCheckout()
{
    boolean enabled = RetailTerminalTable.SelfCheckout ? false : true;

    LoginLogoff.enabled(enabled);
    TaskRecorder.enabled(enabled);
    HardwareStation.enabled(enabled);
}

/// <summary>
/// Update database components and form controls if the SelfCheckout toggle is on.
/// </summary>
private void onSelfCheckoutModified()
{
    if (RetailTerminalTable.SelfCheckout)
    {
        RetailTerminalTable.updateSelfCheckout();
        RetailTerminalTable_DS.refresh();
    }

    this.updateControlsForSelfCheckout();
}

public void init()
{
    super();
    SelfCheckout.visible(isSelfCheckOutFeatureEnabled);
}

