}

public void delete()
{
    ttsBegin;

    element.numberSeqTerminalId().formMethodDataSourceDelete();

    super();

    ttsCommit;
}

public void write()
{
    boolean isInsert = RetailTerminalTable.RecId == 0;
    ttsBegin;

    element.numberSeqTerminalId().formMethodDataSourceWrite();

    using (RetailTerminalTableContext context = RetailTerminalTableContext::construct())
    {
        // set the global parameters to avoid changing the signature of the old method
        context.parmSelectedDataGroupRecId(selectedDataGroupRecId);

        super();

        // if in creating a new terminal, update offline database here.
        if (isInsert)
        {
            element.updateSupportOfflineDatabaseComponents();
        }
    }
    ttsCommit;
}

public int active()
{
    int                 ret;
    RetailStoreTable    retailStoreTable;

    ret = super();

    if (ret)
    {
        select firstOnly inventLocationDataAreaId from retailStoreTable
            where retailStoreTable.RecId == RetailTerminalTable.StoreRecId;

        // Set company context for DimensionEntryControl
        if (retailStoreTable.inventLocationDataAreaId)
        {
            DimensionEntryControl.parmCompany(RetailStoreTable.inventLocationDataAreaId);
        }
        else
        {
            DimensionEntryControl.parmCompany(retailStoreTable.company());
        }

        DimensionEntryControl.reactivate();

    }

    OfflineDbProfile.visible(EnableOfflineDb.checked());
    element.hideControlsForRetailTrial();

    // Card not present processing configuration should only be visible if the omni-channel payments feature is enabled.
    this.object(fieldNum(RetailTerminalTable, CardNotPresentProcessingConfiguration)).visible(FeatureStateProvider::isFeatureEnabled(RetailOmniChannelPaymentsFeature::instance()));

    if (element.isBrazilEnabled)
    {
        this.enableFields_BR();
    }

    FiscalInformation.visible(RetailTerminalTable.isBrazilianStoreRegister());

    if (isSelfCheckOutFeatureEnabled)
    {
        element.updateControlsForSelfCheckout();
    }        

    return ret;
}

/// <summary>
/// Based on terminal operation mode, defines which fields are enabled or disable.
/// </summary>
public void enableFields_BR()
{
    switch (RetailTerminalTable.RetailTerminalOperationMode)
    {
        case RetailTerminalOperationMode::EFDocConsumer:
            retailTerminalTable_ds.object(fieldNum(RetailTerminalTable, FiscalDocumentSeries)).enabled(true);
            retailTerminalTable_ds.object(fieldNum(RetailTerminalTable, FiscalDocumentSeriesContingency)).enabled(true);
            retailTerminalTable_ds.object(fieldNum(RetailTerminalTable, FiscalDocumentSeriesEFDoc)).enabled(true);
            RetailTerminalTable_ds.object(fieldNum(RetailTerminalTable, EFRRegisterNumber)).enabled(true);
            break;

        case RetailTerminalOperationMode::ElectronicFiscalReceipt:
            retailTerminalTable_ds.object(fieldNum(RetailTerminalTable, FiscalDocumentSeries)).enabled(false);
            retailTerminalTable_ds.object(fieldNum(RetailTerminalTable, FiscalDocumentSeriesContingency)).enabled(false);
            retailTerminalTable_ds.object(fieldNum(RetailTerminalTable, FiscalDocumentSeriesEFDoc)).enabled(true);
            RetailTerminalTable_ds.object(fieldNum(RetailTerminalTable, EFRRegisterNumber)).enabled(true);
            break;

        default:
            retailTerminalTable_ds.object(fieldNum(RetailTerminalTable, FiscalDocumentSeries)).enabled(false);
            retailTerminalTable_ds.object(fieldNum(RetailTerminalTable, FiscalDocumentSeriesContingency)).enabled(false);
            retailTerminalTable_ds.object(fieldNum(RetailTerminalTable, FiscalDocumentSeriesEFDoc)).enabled(false);
            RetailTerminalTable_ds.object(fieldNum(RetailTerminalTable, EFRRegisterNumber)).enabled(false);
            break;
    }
}

public void modified()
{
    super();

    RetailTerminalTable_ds.enableFields_BR();
}

