/// <summary>
/// Show or hide profile field.
/// </summary>
/// <returns></returns>
public boolean modified()
{
    boolean ret;

    // All calls to table have to be within RetailTerminalTableContext scope
    using (RetailTerminalTableContext context = RetailTerminalTableContext::construct())
    {
        if (!RetailCdxFeatureControl::isDataGroupSelectionForNewOfflineSupportDisabled())
        {
            // when enabling offline if there are more than one data group, pop up the selector form to select which data group to use
            if (EnableOfflineDb.value() && !retailTerminalTable.OfflineDatabaseProfile)
            {
                RetailCDXDataGroup retailCDXDataGroup;

                select count(RecId) from retailCDXDataGroup;

                // only pop up the data group selector form if there are more than one data group
                if (retailCDXDataGroup.RecId > 1)
                {
                    Args dataGroupSelectorArgs = new Args();
                    dataGroupSelectorArgs.record(retailTerminalTable);
                    FormRun dataGroupSelectorFormRun = new MenuFunction(menuItemDisplayStr(RetailCDXDataGroupSelector), MenuItemType::Display).create(dataGroupSelectorArgs);

                    dataGroupSelectorFormRun.run();
                    dataGroupSelectorFormRun.wait();

                    selectedDataGroupRecId = str2Int64(dataGroupSelectorArgs.parm());

                    if (selectedDataGroupRecId)
                    {
                        // set the global parameters to avoid changing the signature of the old method
                        context.parmSelectedDataGroupRecId(selectedDataGroupRecId);
                    }
                    else
                    {
                        // if no data group is selected, abort the change
                        warning("@Retail:RetailTerminalTableEnableOfflineWarning");
                        return false;
                    }
                }
            }
        }

        ret = super();
        ret = ret && RetailTerminalTable.validateCardNotPresentConfiguration();

    // if in creating a new terminal, do not update offline database here.
    if (ret && RetailTerminalTable.RecId != 0)
    {
        element.updateSupportOfflineDatabaseComponents();
    }
    }

    return ret;
}

