/// <summary>
/// Throws error if multiple hardware station options selected.
/// </summary>
/// <returns>True if the validation called on modified is successful; false otherwise.</returns>
public boolean modified()
{
    // All calls to table have to be within RetailTerminalTableContext scope
    using (RetailTerminalTableContext context = RetailTerminalTableContext::construct())
    {
        boolean ret;

        if (HardwareStation_SelectHardwareStationOnTendering.checked() && HardwareStation_SelectHardwareStationAtStartOfTransaction.checked())
        {
            throw Error("@Retail:RetailSelectHardwareStationError");
        }

        ret = super();
        return ret;
    }
}

