public Common createLookup(FormReferenceControl _formReferenceControl, RetailZoneType _zoneType)
{
    SysReferenceTableLookup sysTableLookup;
    Query query;
    QueryBuildDataSource qbDS;

    sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(RetailTillLayoutZone), _formReferenceControl, true);
    sysTableLookup.addLookupfield(fieldNum(RetailTillLayoutZone, ZoneId));
    sysTableLookup.addLookupfield(fieldNum(RetailTillLayoutZone, ZoneName));

    query = new Query();
    qbDS = query.addDataSource(tableNum(RetailTillLayoutZone));

    qbDS.addRange(fieldNum(RetailTillLayoutZone, LayoutType)).value(queryValue(RetailTillLayoutSize.LayoutType));
    qbDS.addRange(fieldNum(RetailTillLayoutZone, ZoneType)).value(queryValue(_zoneType));

    sysTableLookup.parmQuery(query);

    return sysTableLookup.performFormLookup();
}

