/// <summary>
/// Enable/disable the "Add" button for dependent data sources depending on if the required parent exists.
/// </summary>
public void SetAddButtonState()
{
    boolean requiredParentExist;

    // Enable "Add" of a group line if it's an existing group
    requiredParentExist = (RetailTillLayout.RecId != 0);
    RetailTillLayoutButtonGridZone_DS.allowCreate(requiredParentExist);
    RetailDeviceTypes_DS.allowCreate(requiredParentExist);
    RetailTillLayoutImageZone_DS.allowCreate(requiredParentExist);
    RetailTillLayoutReportZone_DS.allowCreate(requiredParentExist);
}

public int active()
{
    int ret;

    ret = super();

    if (!RetailTillLayout)
    {
        Duplicate.enabled(false);
    }
    else
    {
        Duplicate.enabled(true);
    }

    this.SetAddButtonState();

    str layoutId = RetailTillLayout.layoutId;

    RetailTillLayoutButtonGridZone_DS.query().dataSourceTable(tableNum(RetailTillLayoutButtonGridZone)).clearRanges();
    RetailTillLayoutImageZone_DS.query().dataSourceTable(tableNum(RetailTillLayoutImageZone)).clearRanges();
    RetailTillLayoutButtonGridZone_DS.query().dataSourceTable(tableNum(RetailTillLayoutButtonGridZone)).addRange(fieldNum(RetailTillLayoutButtonGridZone, LayoutId)).value(layoutId);
    RetailTillLayoutImageZone_DS.query().dataSourceTable(tableNum(RetailTillLayoutImageZone)).addRange(fieldNum(RetailTillLayoutImageZone, LayoutId)).value(layoutId);

    LayoutDesignerButton.enabled(false);
    DesignButtonGrid.enabled(false);

    return ret;
}

public void initValue()
{
    super();

    if (duplicateClicked)
    {
        RetailTillLayout.name                       = retailTillLayoutLoc.name;
        RetailTillLayout.DeviceType                 = retailTillLayoutLoc.DeviceType;
    }
    else
    {
        // Changes in App Fall elimtate the need to distinguish between Cloud POS and MPOS for Screen Layouts and since EPOS is not in scope, this field should be hidden (or removed).
        // This will likely come back when EPOS is supported for N-1, but until then it is not needed and the Cloud POS/MPOS options are confusing.
        RetailDeviceTypes deviceType;
        select firstonly RecId from deviceType
            where deviceType.Type == RetailDeviceTypeBase::ModernPOS;

        RetailTillLayout.DeviceType = deviceType.RecId;
    }
}

