void clicked()
{
    RetailTillLayoutButtonGridZone  buttonGridZone;
    RetailTillLayoutImageZone       imageZone;
    RetailTillLayoutReportZone      reportZone;
    RetailTillLayoutConfiguration   layoutConfiguration;
    RetailLayoutId                  newLayoutId;
    Dialog                          dialog;
    DialogField                     dialogFieldLayoutId;
    RetailTillLayout retailTillLayoutIdSearch;

    super();

    // prompt for a new layout id
    while (true)
    {
        dialog = new Dialog();
        dialogFieldLayoutId = dialog.addField('RetailLayoutId', "@REX4520113");
        if (dialog.run())
        {
            newLayoutId = dialogFieldLayoutId.value();

            // system prevents the creation of a screen layout with an empty layout ID
            if (strLRTrim(newLayoutId) == '')
            {
                error("@REX4520992");
            }
            else
            {
                select firstOnly layoutId from retailTillLayoutIdSearch
                where retailTillLayoutIdSearch.layoutId == newLayoutId;

                if (!retailTillLayoutIdSearch)
                {
                    // id does not exist, continue to insert.
                    break;
                }

                error("@REX4520114");
            }
        }
        else
        {
            // dialog cancelled, exit.
            return;
        }
    }

    duplicateClicked = true;
    retailTillLayoutLoc.data(RetailTillLayout);
    RetailTillLayout.layoutId = newLayoutId;
    RetailTillLayout.insert();

    // Duplicate all the associated layout sizes
    while select layoutConfiguration
        where layoutConfiguration.LayoutId == retailTillLayoutLoc.layoutId
    {
        RetailTillLayoutConfiguration.data(layoutConfiguration);
        RetailTillLayoutConfiguration.LayoutId = RetailTillLayout.layoutId;
        RetailTillLayoutConfiguration.insert();

        // Duplicate all the associated button grid zones
        while select buttonGridZone
            where buttonGridZone.LayoutId == layoutConfiguration.layoutId &&
                buttonGridZone.LayoutSizeId == layoutConfiguration.SizeId

        {
            RetailTillLayoutButtonGridZone.data(buttonGridZone);
            RetailTillLayoutButtonGridZone.LayoutId = RetailTillLayout.layoutId;
            RetailTillLayoutButtonGridZone.insert();
        }

        // Duplicate all the associated image zones
        while select imageZone
            where imageZone.LayoutId == layoutConfiguration.layoutId &&
                imageZone.LayoutSizeId == layoutConfiguration.SizeId
        {
            RetailTillLayoutImageZone.data(imageZone);
            RetailTillLayoutImageZone.LayoutId = RetailTillLayout.layoutId;
            RetailTillLayoutImageZone.insert();
        }

        // Duplicate all the associated report zones
        while select reportZone
            where reportZone.LayoutId == layoutConfiguration.layoutId &&
                reportZone.LayoutSizeId == layoutConfiguration.SizeId
        {
            RetailTillLayoutReportZone.data(reportZone);
            RetailTillLayoutReportZone.LayoutId = RetailTillLayout.layoutId;
            RetailTillLayoutReportZone.insert();
        }
    }

    duplicateClicked = false;
}

/// <summary>
/// Launch the till layout click-once application
/// </summary>
public void clicked()
{
    str parameters;
    str navigationUrl;
    RetailDesignerFormEnum targetedDesigner;
    boolean isLegacyDesigner;

    Browser browser = new Browser();

    super();

    targetedDesigner = RetailClickOnceHelper::TargetedDesignerForm(RetailTillLayoutSize);
    isLegacyDesigner = (targetedDesigner == RetailDesignerFormEnum::RetailTillLayoutDesigner);

    if (isLegacyDesigner)
    {
        // Legacy
        navigationUrl = RetailClickOnceHelper::GetTillLayoutDesignerClickOnceURI(RetailTillLayout.layoutId, RetailTillLayoutConfiguration.SizeId);
    }
    else
    {
        // Modern
        navigationUrl = RetailClickOnceHelper::GetTillLayoutDesignerModernClickOnceURI_V2(RetailTillLayoutConfiguration.layoutId, RetailTillLayoutConfiguration.SizeId);
    }

    browser.navigate(navigationUrl, true, false);
}

