/// <summary>
///Refresh the signature display
/// </summary>
public void RefreshSignature()
{
    //X++ image variables
    Binary binaryImage;
    Image  image;

    // .Net drawing variables
    int sigPadding = 10;
    int imageHeight = SignatureImageControl.heightValue(); //300;
    int imageWidth = SignatureImageControl.widthValue(); //600;

    // Signature data for payment trans record
    str sigData = retailTransactionPaymentTrans.sigCapData;

    // Interop asserts
    InteropPermission perm = new InteropPermission(InteropKind::ClrInterop);
    perm.assert();

    // create a bitmap and a memory stream weâ€™ll use for in memory conversion
    System.Drawing.Image sigImage;
    System.IO.MemoryStream myStream = new System.IO.MemoryStream();

    try
    {
        // draw the image
        sigImage = RetailSignatureViewerHelper::DrawSignature(sigData, sigpadding, imageheight, imagewidth);

        if (sigImage)
        {
            // if the image exists, convert to bitmap in-memory stream
            sigImage.Save(myStream, System.Drawing.Imaging.ImageFormat::Bmp);

            // stream goes to bitmap
            binaryImage = Binary::constructFromMemoryStream(myStream);

            // bitmap goes to image (needed for the control)
            image = new Image();
            image.setData(binaryImage.getContainer());

            // show the image on the screen with the image control
            SignatureImageControl.image(image);
        }
        else
        {
            // otherwise, clear the image.
            SignatureImageControl.image(null);
        }
    }
    finally
    {
        // Dispose of .Net objects
        if (sigImage != null)
        {
            sigImage.Dispose();
        }

        if (myStream != null)
        {
            myStream.Dispose();
        }
    }

    CodeAccessPermission::revertAssert();
}

/// <summary>
/// Shows/hides force void elements on the form.
/// </summary>
public void showHideForceVoidElements()
{
    retailTransactionPaymentTrans_ds.reread();
    retailTransactionPaymentTrans_ds.refresh();

    boolean showForceVoidElements = (retailTransactionPaymentTrans.VoidStatus == RetailTenderVoidStatus::ForceVoided ||
        retailTransactionPaymentTrans.VoidStatus == RetailTenderVoidStatus::ForceVoidReconciled);
    ReconcileButton.visible(showForceVoidElements);
    TabForceVoidReconciledTransactionsLog.visible(showForceVoidElements);

    if (showForceVoidElements)
    {
        retailReconcilePaymentSource_ds.research();
        retailReconcilePaymentSource_ds.refreshEx();
    }

}

private void updateDataSource()
{
    QueryBuildRange rangeCaptureFailed;

    if (element.args() &&
        element.args().menuItemName() == menuitemDisplayStr(RetailTransactionPaymentTransCaptureFail))
    {
        rangeCaptureFailed = SysQuery::findOrCreateRange(RetailTransactionPaymentTrans_ds.queryBuildDataSource(), fieldNum(RetailTransactionPaymentTrans, IsCaptureFailed));
        rangeCaptureFailed.status(RangeStatus::Locked);
        rangeCaptureFailed.value(queryValue(1));
    }
}

public void selectionChanged()
{
    super();

    element.RefreshSignature();
    element.showHideForceVoidElements();
}

public int active()
{
    int ret = super();
    element.setButtonAccess();

    if (EnhancedWalletSupportAndPaymentsImprovementFeature::isEnabled())
    {
        ApprovalCode.visible(true);
        ProcessorPaymentMethod.visible(true);
        ProcessorReference.visible(true);
    }

    return ret;
}

public void init()
{
    super();

    RetailStatementMethod statementMethod;
    statement = this.query().dataSourceNo(1).addRange(fieldnum(RetailTransactionPaymentTrans, statementId));

    if (element.args().caller() && element.args().dataset() == tablenum(RetailStatementTrans))
    {
        RetailStatementTrans statementTrans = element.args().record();

        if (RetailStatementJour::find(statementTrans.statementId).StatementType == RetailStatementType::Financial)
        {
            statementMethod = RetailStatementJour::find(statementTrans.statementId).statementMethod;
            this.executeInitQuery(statementMethod, statementTrans.statementId, statementTrans.statementCode, statementTrans.currency, statementTrans.cardTypeId, statementTrans.TenderTypeId);
        }
    }

    if (element.args().caller() && element.args().dataset() == tablenum(RetailStatementLine))
    {
        RetailStatementLine statementLine = element.args().record();

        if (RetailStatementTable::find(statementLine.statementId).StatementType == RetailStatementType::Financial)
        {
            statementMethod = RetailStatementTable::find(statementLine.statementId).statementMethod;
            this.executeInitQuery(statementMethod, statementLine.statementId, statementLine.statementCode, statementLine.currency, statementLine.cardTypeId, statementLine.TenderTypeId);
        }
    }
}

private void executeInitQuery(RetailStatementMethod _statementMethod, RetailStatementId _statementId, RetailStatementCode _statementCode, CurrencyCode _currency, RetailCardTypeId _cardTypeId, RetailTenderTypeId _tenderTypeId)
{
    QueryBuildDataSource retailTransPaymentTransQbds = RetailTransactionPaymentTrans_ds.query().dataSourceName(RetailTransactionPaymentTrans_ds.name());
    retailTransPaymentTransQbds.clearRanges();
    retailTransPaymentTransQbds.clearLinks();
    retailTransPaymentTransQbds.clearDynalinks();

    retailTransPaymentTransQbds.addRange(fieldnum(RetailTransactionPaymentTrans, Currency)).value(queryValue(_currency));
    retailTransPaymentTransQbds.addRange(fieldnum(RetailTransactionPaymentTrans, CardTypeId)).value(queryValue(_cardTypeId));
    retailTransPaymentTransQbds.addRange(fieldnum(RetailTransactionPaymentTrans, TenderType)).value(queryValue(_tenderTypeId));

    QueryBuildDataSource retailTransactionTableQbds = retailTransPaymentTransQbds.addDataSource(tableNum(RetailTransactionTable));
    retailTransactionTableQbds.relations(true);
    retailTransactionTableQbds.joinMode(JoinMode::InnerJoin);

    switch (_statementMethod)
    {
        case RetailStaffTerminalTotal::Staff:
            retailTransPaymentTransQbds.addRange(fieldnum(RetailTransactionPaymentTrans, Staff)).value(queryValue(_statementCode));
            break;

        case RetailStaffTerminalTotal::Terminal:
            retailTransPaymentTransQbds.addRange(fieldnum(RetailTransactionPaymentTrans, Terminal)).value(queryValue(_statementCode));
            break;

        case RetailStaffTerminalTotal::Shift:
            retailTransactionTableQbds.addRange(fieldnum(RetailTransactionTable, BatchTerminalId)).value(queryValue(RetailStatementLine::getBatchTerminalId(_statementCode)));
            retailTransactionTableQbds.addRange(fieldnum(RetailTransactionTable, BatchId)).value(queryValue(RetailStatementLine::getBatchId(_statementCode)));
            break;

        case RetailStaffTerminalTotal::Total:
            break;
    }

    QueryBuildDataSource retailPOSBatchTableQbds = retailTransactionTableQbds.addDataSource(tableNum(RetailPosBatchTable));
    retailPOSBatchTableQbds.relations(true);
    retailPOSBatchTableQbds.joinMode(JoinMode::InnerJoin);
    statement = retailPOSBatchTableQbds.addRange(fieldnum(RetailPosBatchTable, StatementId));
}

public void executeQuery()
{
    ;
    statement.value(rboStatementId);

    super();

    element.setButtonAccess();
}

public void linkActive()
{
    ;

    if (element.args().caller())
    {
        if (element.args().dataset() == tablenum(RetailStatementTrans))
        {
            rboStatementTrans = element.args().record();
            rboStatementId = rboStatementTrans.statementId;
        }

        if (element.args().dataset() == tablenum(RetailStatementLine))
        {
            RetailStatementLine statementLine = element.args().record();
            rboStatementId = statementLine.statementId;
        }
    }

    super();
}

/// <summary>
/// Show popup dialog where user can reconcile forced void payment transaction.
/// </summary>
public void clicked()
{
    super();
    Args args = new Args(formstr(RetailReconcilePaymentSlider));
    args.record(retailTransactionPaymentTrans);
    MenuFunction menuFunction =
            new MenuFunction(menuitemDisplayStr(RetailReconcilePaymentSlider), MenuItemType::Display);

    FormRun formRun = menuFunction.create(args);
    formRun.run();
    formRun.wait();
    element.showHideForceVoidElements();
}
