private boolean isCallerEnumParmRetailChannelType()
{
    return element.args() && element.args().parmEnumType() == enumNum(RetailChannelType);
}

private void enableDisableFields()
{
    if (this.isCallerEnumParmRetailChannelType())
    {
        switch(element.args().parmEnum())
        {
            case RetailChannelType::SharePointOnlineStore:
                transactionTable_ds.object(Fieldnum(RetailTransactionTable, Store)).visible(false);
                transactionTable_ds.object(Fieldnum(RetailTransactionTable, entryStatus)).visible(false);
                transactionTable_ds.object(Fieldnum(RetailTransactionTable, receiptId)).visible(false);
                transactionTable_ds.object(Fieldnum(RetailTransactionTable, terminal)).visible(false);
                Functions.enabled(false);

                break;

            case RetailChannelType::RetailStore:
                element.setOrderStatusControlsVisibility(false);

                break;

            default:
                // The default view.
                // This is to accomodate multiple places inside AX that call RetailTransactionTable form directly, instead of menu display item.
                element.setOrderStatusControlsVisibility(false);
                Functions.enabled(false);

                break;
        }
    }
}

private void setOrderStatusControlsVisibility(boolean _visible)
{
    retailTransactionOrderStatus_ds.object(Fieldnum(RetailTransactionOrderStatus, SalesId)).visible(_visible);
    retailTransactionOrderStatus_ds.object(Fieldnum(RetailTransactionOrderStatus, Status)).visible(_visible);
    OrderStatusFieldGroup.visible(_visible);
}

public void init()
{
    callerMenuItemName = element.args().menuItemName();

    params = new RetailReportParameters("");
    params.parmEnableTopCount(false);
    params.parmEnableTypeSelect(true);

    if (callerMenuItemName == menuitemDisplayStr(RetailOnlineStoreTransactionTable))
    {
        params.parmEnableOnlineStore(true);
        params.parmEnableTypeSelect(false);
    }

    super();
    element.enableDisableFields();

    if (element.args().caller()     &&
        element.args().record().TableId == tableNum(SalesTable))
    {
        transactionIncomeExpenseTrans.visible(false);
        transactionTenderDeclarationTrans.visible(false);
        transactionBankedTenderTrans.visible(false);
        transactionSafeTenderTrans.visible(false);
        transactionOrderInvoiceTrans.visible(false);
    }
    if (RetailHideMoveTransactionsToNewShiftButtonFlight::instance().isEnabled())
    {
        RetailChangeTransactionShift.visible(false);
    }

    // <GBR>
    if (BrazilParameters::isEnabled()
        && element.args().caller()
        && element.args().record().TableId == tableNum(FiscalDocument_BR))
    {
        this.initByFiscalDocument_BR();
    }
    // </GBR>
}

private void initByFiscalDocument_BR()
{
    FiscalDocument_BR   fiscalDocument_BR;

    fiscalDocument_BR = element.args().record();
    transactionTable_ds.queryBuildDataSource().addRange(fieldNum(RetailTransactionTable, RecId)).value(SysQuery::value(fiscalDocument_BR.RefRecId));
    transactionTable_ds.executeQuery();
}

/// <summary>
/// Adds predefined filters to the documents generated using "Open in Excel"
/// </summary>
/// <param name = "_menuItem">The menu item</param>
/// <returns>A map with the filters being applied.</returns>
public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);

    if (_menuItem.templateId() == resourceStr(RetailTransactionAuditableWorkbook)
     || _menuItem.templateId() == resourceStr(RetailTransactionAuditableWorkbookCustomerOrder))
    {
        str headerEntityName = tableStr(RetailTransactionAuditableEntity);

        OMOperatingUnit operatingUnit = OMOperatingUnit::find(retailChannelTable.OMOperatingUnitID, OMOperatingUnitType::RetailChannel);

        // Create header filter
        ExportToExcelFilterTreeBuilder headerFilterBuilder = new ExportToExcelFilterTreeBuilder(headerEntityName);

        Microsoft.Dynamics.Platform.Integration.Office.FilterCollectionNode collectionNode = new Microsoft.Dynamics.Platform.Integration.Office.FilterCollectionNode();
        collectionNode.Operator = Microsoft.Dynamics.Platform.Integration.Office.FilterOperators::And;

        var collection = collectionNode.Collection;
        collection.Add(headerFilterBuilder.areEqual(fieldStr(RetailTransactionAuditableEntity, OperatingUnitNumber), operatingUnit.OMOperatingUnitNumber));
        collection.Add(headerFilterBuilder.areEqual(fieldStr(RetailTransactionAuditableEntity, StoreNumber),         transactionTable.Store));
        collection.Add(headerFilterBuilder.areEqual(fieldStr(RetailTransactionAuditableEntity, RegisterNumber),      transactionTable.terminal));
        collection.Add(headerFilterBuilder.areEqual(fieldStr(RetailTransactionAuditableEntity, TransactionId),       transactionTable.TransactionId));

        filtersToApply.insert(headerEntityName, collectionNode);
    }

    return filtersToApply;
}

