/// <summary>
/// Customizes the "Open in Excel" experience in the form.
/// </summary>
/// <param name = "_menuOptions">The menu options.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();
        if (dataEntityOptions.dataEntityName() == tableStr(RetailPosBatchEntity) || dataEntityOptions.dataEntityName() == tableStr(RetailPosBatch2Entity))
        {
            dataEntityOptionsIterator.delete();
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }

    // The 3 menu items below are mutually exclusive, if one is visible the other 2 are hidden
    // They share the same label because from a user perspective they should provide pretty much the same experience.

    // Add the Office Menu item that supports Cash and Carry
    editSelectedTransactionEntityMenuItem = OfficeTemplateExportMenuItem::construct(OfficeAppApplicationType::Excel, resourceStr(RetailTransactionAuditableWorkbook), RetailTransactionAuditableTemplateManager::EditSelectedCashAndCarryTransactionExcelWorkbook);
    editSelectedTransactionEntityMenuItem.visible(RetailTransactionAuditableTemplateManager::isRetailTransactionAuditableWorkbookVisible(transactionTable));
    editSelectedTransactionEntityMenuItem.displayName("@RetailAudit:EditSingleTransactionWorkbookLabel");
    editSelectedTransactionEntityMenuItem.description("@RetailAudit:EditSingleTransactionWorkbookDescription");

    _menuOptions.customMenuItems().addEnd(editSelectedTransactionEntityMenuItem);

    // Add the Office Menu item that supports Async customer orders
    editSelectedAsyncCustomerOrderEntityMenuItem = OfficeTemplateExportMenuItem::construct(OfficeAppApplicationType::Excel, resourceStr(RetailTransactionAuditableWorkbookCustomerOrder), RetailTransactionAuditableTemplateManager::EditSelectedAsyncCustomerOrderExcelWorkbook);
    editSelectedAsyncCustomerOrderEntityMenuItem.visible(RetailTransactionAuditableTemplateManager::isRetailTransactionAuditableWorkbookCustomerOrderVisible(transactionTable));
    editSelectedAsyncCustomerOrderEntityMenuItem.displayName("@RetailAudit:EditSingleTransactionWorkbookLabel");
    editSelectedAsyncCustomerOrderEntityMenuItem.description("@RetailAudit:EditSingleTransactionWorkbookDescription");

    _menuOptions.customMenuItems().addEnd(editSelectedAsyncCustomerOrderEntityMenuItem);

    // Add the Office Menu item that supports Online orders
    editSelectedOnlineTransactionEntityMenuItem = OfficeGeneratedExportMenuItem::construct(tableStr(RetailTransactionAuditableEntity), RetailTransactionAuditableTemplateManager::EditOnlineTransactionExcelWorkbook);
    editSelectedOnlineTransactionEntityMenuItem.visible(RetailTransactionAuditableTemplateManager::isSimplifiedAuditableWorkbookVisible(transactionTable));
    editSelectedOnlineTransactionEntityMenuItem.displayName("@RetailAudit:EditSingleTransactionWorkbookLabel");
    editSelectedOnlineTransactionEntityMenuItem.description("@RetailAudit:EditSingleTransactionWorkbookDescription");

    _menuOptions.customMenuItems().addEnd(editSelectedOnlineTransactionEntityMenuItem);
}

/// <summary>
/// Gets the <see cref="ExportToExcelDataEntityContext"/> that specifies what should be exported for the specifed menu item.
/// </summary>
/// <param name="_menuItem">The menu item.</param>
/// <returns>
/// Returns the <see cref="ExportToExcelDataEntityContext"/> that specifies what should be exported for the specifed menu item;
/// otherwise, null.
/// </returns>
public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case RetailTransactionAuditableTemplateManager::EditOnlineTransactionExcelWorkbook:
            context = RetailTransactionAuditableExcelContextFactory::constructOnlineTransactionContext(transactionTable);
            break;

        default:
            throw error(error::wrongUseOfFunction(funcName()));
    }

    return context;
}

public void init()
{
    HcmWorker hcmWorker;

    super();

    storeRange =
        this.queryBuildDataSource().addRange(fieldnum(RetailTransactionTable, store));

    if (params.parmEnableOnlineStore())
    {
        channelRange = this.queryBuildDataSource().addRange(fieldnum(RetailTransactionTable, Channel));
    }

    dateRange =
        this.queryBuildDataSource().addRange(fieldnum(RetailTransactionTable, transDate));
    offlineRange =
        this.queryBuildDataSource().addRange(fieldnum(RetailTransactionTable, CreatedOffline));

    if (element.args() &&
        element.args().caller() &&
        element.args().record())
    {
        if (element.args().record().TableId == tableNum(SalesTable))
        {
            this.query().dataSourceTable(tableNum(RetailTransactionTable)).clearDynalinks();
        }
        else if (element.args().record().TableId == tableNum(HcmWorker))
        {
            // Scope transactions down to the worker selected.
            hcmWorker = element.args().record();

            this.query().dataSourceTable(tableNum(RetailTransactionTable)).addRange(fieldNum(RetailTransactionTable, staff)).value(queryValue(hcmWorker.PersonnelNumber));
        }
    }

    if (callerMenuItemName == menuitemDisplayStr(RetailForcedVoidTransactions))
    {
        QueryBuildDataSource qbdsPaymentTrans = this.queryBuildDataSource().addDataSource(tableNum(RetailTransactionPaymentTrans));
        qbdsPaymentTrans.joinMode(JoinMode::ExistsJoin);
        qbdsPaymentTrans.relations(true);
        qbdsPaymentTrans.addRange(fieldNum(RetailTransactionPaymentTrans, VoidStatus)).value(queryValue(RetailTenderVoidStatus::ForceVoided));
    }
}

public int active()
{
    int ret = super();

    //<GBR>
    if (BrazilParameters::isEnabled())
    {
        RetailFiscalReceiptButton.enabled(transactionTable.type == RetailTransactionType::Sales);
    }
    //</GBR>

    // Validation errors button should be only enabled when there is at least one error
    ConsistencyCheckerErrors.enabled(transactionTable.ValidationStatus == RetailTransactionValidationStatus::Failed);

    // Only show sales group when transaction is of Types: "Sales" OR "CustomerOrder" OR "AsyncCustomerOrder" OR "AsyncCustomerQuote"
    TransactionOrigin_salesGroup.visible(
        transactionTable.type == RetailTransactionType::Sales
        || transactionTable.type == RetailTransactionType::CustomerOrder
        || transactionTable.type == RetailTransactionType::AsyncCustomerOrder
        || transactionTable.type == RetailTransactionType::AsyncCustomerQuote
        );

    if (editSelectedTransactionEntityMenuItem)
    {
        editSelectedTransactionEntityMenuItem.visible(RetailTransactionAuditableTemplateManager::isRetailTransactionAuditableWorkbookVisible(transactionTable));
    }

    if (editSelectedAsyncCustomerOrderEntityMenuItem)
    {
        editSelectedAsyncCustomerOrderEntityMenuItem.visible(RetailTransactionAuditableTemplateManager::isRetailTransactionAuditableWorkbookCustomerOrderVisible(transactionTable));
    }

    if (editSelectedOnlineTransactionEntityMenuItem)
    {
        editSelectedOnlineTransactionEntityMenuItem.visible(RetailTransactionAuditableTemplateManager::isSimplifiedAuditableWorkbookVisible(transactionTable));
    }

    element.setOrderStatusControlsVisibility(this.currentTransactionHasOrderStatus());
    SyncSingleTransaction.enabled(this.canBeSynchronized());
    ValidateSingleTransaction.enabled(this.canBeValidated());

    return ret;
}

private boolean currentTransactionHasOrderStatus()
{
    return RetailTransactionAsyncOrdersHelper::isAsyncTransactionType(transactionTable.type);
}

private boolean canBeSynchronized()
{
    return this.currentTransactionHasOrderStatus() && retailTransactionOrderStatus.Status != RetailTransactionOrderStatusType::Succeeded;
}

private boolean canBeValidated()
{
    return (transactionTable.type == RetailTransactionType::Sales
        || transactionTable.type == RetailTransactionType::PendingSalesOrder
        || transactionTable.type == RetailTransactionType::AsyncCustomerOrder
        || transactionTable.type == RetailTransactionType::AsyncCustomerQuote)
        && transactionTable.ValidationStatus == RetailTransactionValidationStatus::Failed;

}

private boolean shouldSkipDefaultFilter()
{
    boolean result = false;

    if (element.args() && element.args().caller())
    {
        FormRun caller = element.args().caller() as FormRun;

        if (caller && caller.args() && caller.args().name())
        {
            str transactionsFormCallerName = caller.args().name();

            result = (transactionsFormCallerName == tableId2Name(tableNum(RetailStatementTable))) ||
                (transactionsFormCallerName == tableId2Name(tableNum(RetailStatementJour))) ||
                (transactionsFormCallerName == tableId2Name(tableNum(SalesTable))) ||
                (callerMenuItemName == menuItemDisplayStr(RetailTransactionsFailingValidationTileMenuItem));
        }
    }

    return result;
}

/// <summary>
///Executes the query.
/// </summary>
public void executeQuery()
{
    if (!params.untouched() && !this.shouldSkipDefaultFilter())
    {
        storeRange.value(params.parmStoreId());

        if (params.parmEnableOnlineStore() && params.parmOnlineStoreId())
        {
            RetailChannelview retailChannelView;

            select RecId from retailChannelView where retailChannelView.Name == params.parmOnlineStoreId();

            channelRange.value(Int642Str(retailChannelView.RecId));
        }

        date start = params.parmDateFrom();
        date end = params.parmDateTo();
        dateRange.value(SysQuery::range(start, end));
        if (params.parmTypeSelect() == RetailReportType::Offline)
        {
            offlineRange.value("1");
        }
        else
        {
            offlineRange.value("");
        }
    }

    super();
}

/// <summary>
/// Gets the completed sales receiptId of a suspended transaction.
/// </summary>
/// <param name="_retailTransactionTable">The RetailTransactionTable buffer.</param>
/// <returns>Returns receipt number.</returns>
display RetailReceiptId referenceReceiptId(RetailTransactionTable _retailTransactionTable)
{
    RetailTransactionTable suspendedTransactionTable;
    str receiptNum = '';

    if (_retailTransactionTable.type ==  RetailTransactionType::SuspendedTransaction)
    {
        select firstOnly receiptId from suspendedTransactionTable
            where suspendedTransactionTable.Channel == _retailTransactionTable.Channel
            && suspendedTransactionTable.transactionId == _retailTransactionTable.SuspendedTransactionId;

        receiptNum = suspendedTransactionTable.receiptId;
    }

    return receiptNum;
}

/// <summary>
/// Opens the sales quotations form if quotaion is created from POS.
/// </summary>
public void jumpRef()
{
    if (transactionTable.TransactionOrderType == RetailTransactionOrderTypes::Quote)
    {
        SalesQuotationTable salesQuotationTable;
        Args quoteArgs = new Args();

        quoteArgs.caller(this);
        salesQuotationTable = SalesQuotationTable::find(transactionTable.salesOrderId);
        quoteArgs.record(salesQuotationTable);
        quoteArgs.lookupRecord(salesQuotationTable);

        new MenuFunction(menuitemDisplayStr(SalesQuotationTable), MenuItemType::Display).run(quoteArgs);
    }
    else
    {
        super();
    }
}

public void jumpRef()
{
    Args args = new Args();
    args.caller(this);

    // Search open statement firstly
    RetailStatementTable openStatement = RetailStatementTable::find(transactionTable.statementId);

    if (openStatement)
    {
        if (isConfigurationkeyEnabled(configurationKeyNum(RetailEodBackwardCompatibility)))
        {
            Args.record(openStatement);

            new MenuFunction(menuItemDisplayStr(RetailStatementTable), MenuItemType::Display).run(args);
        }
    }
    else
    {
        // Search posted statement
        RetailStatementJour postedStatement = RetailStatementJour::find(transactionTable.statementId);

        if (postedStatement)
        {
            Args.record(postedStatement);
            new MenuFunction(menuitemdisplaystr(RetailStatementJour), MenuItemType::Display).run(args);
        }
    }
}

public void jumpRef()
{
    Args args = new Args();

    args.record(RetailStoreTable::find(transactionTable.store));

    new MenuFunction(menuitemDisplayStr(RetailStoreTableDetails), MenuItemType::Display).run(args);
}

public void init()
{
    RetailPosBatchTable loc_RetailPosBatchTable;

    super();

    if (element.args() &&
        element.args().record())
    {
        // When the form is invoked from the RetailPosBatchTable table, scope transactions to the current shift time range.
        if (element.args().record().TableId == tableNum(RetailPosBatchTable))
        {
            loc_RetailPosBatchTable = element.args().record();

            params.parmDateFrom(loc_RetailPosBatchTable.StartDate);
            params.parmDateTo(loc_RetailPosBatchTable.CloseDate);
        }
    }
}

public void init()
{
    super();

    // If the caller has a  enum of type RetailChannelType as its parameter
    // then filter the RetailChannelTable datasource based on the channel type parameter.
    if (element.isCallerEnumParmRetailChannelType())
    {
        RetailChannelType channelType = element.args().parmEnum();

        this.query().dataSourceTable(tableNum(RetailChannelTable)).addRange(fieldNum(RetailChannelTable, channeltype)).value(queryValue(channelType));
    }

    // The default view will have no filtering.
    // This is to accomodate multiple places inside AX that call RetailTransactionTable form directly.
}

public void init()
{
    SalesTable loc_SalesTable;
    RetailTransactionTable loc_RetailTransactionTable;

    super();

    if (element.args() &&
        element.args().record())
    {
        // When the form is invoked from the SalesOrder table, scope transactions to the current sales order.
        if (element.args().record().TableId == tableNum(SalesTable))
        {
            loc_SalesTable = element.args().record();

            // Check if sales order originated from CRT or POS
            select firstOnly RecId from loc_RetailTransactionTable where loc_RetailTransactionTable.salesOrderId == loc_SalesTable.SalesId;

            if (loc_RetailTransactionTable.RecId)
            {
                // When the sales order originated from POS.
                this.query().dataSourceTable(tableNum(RetailTransactionTable)).addRange(fieldNum(RetailTransactionTable, salesOrderId)).value(queryValue(loc_SalesTable.SalesId));
            }
            else
            {
                // When the sales order originated from CRT.
                this.query().dataSourceTable(tableNum(RetailTransactionOrderStatus)).joinMode(JoinMode::InnerJoin);
                this.query().dataSourceTable(tableNum(RetailTransactionOrderStatus)).addRange(fieldNum(RetailTransactionOrderStatus, SalesId)).value(queryValue(loc_SalesTable.SalesId));
            }
        }
    }
}

