    this.enableFiscalTransRelatedButtons();
    return ret;
}

/// <summary>
/// Determines if enable buttons related to fiscal transactions.
/// </summary>
private void enableFiscalTransRelatedButtons()
{
    retailTransactionInfocodeTrans.enabled(RetailTransactionFiscalTrans.existRetailTransactionInfoCodeTrans());
    retailTransactionFiscalTransExtendedData.enabled(RetailTransactionFiscalTrans.existFiscalTransExtendedData());
}

void clicked()
{
    transactionSalesTrans.enabled(false);
    transactionPaymentTrans.enabled(false);
    transactionDiscountTrans.enabled(false);
    transactionIncomeExpenseTrans.enabled(false);
    transactionInfocodeTrans.enabled(false);
    transactionTenderDeclarationTrans.enabled(false);
    transactionBankedTenderTrans.enabled(false);
    transactionSafeTenderTrans.enabled(false);
    transactionLoyaltyRewardPointTrans.enabled(false);
    transactionOrderInvoiceTrans.enabled(false);
    transactionRetailLog.enabled(false);
    transactionSupplementaryInvoice.enabled(false);

    // each menu item disabled unless proved otherwise
    if ((select firstonly recId from RetailTransactionSalesTrans
            where RetailTransactionSalesTrans.store         == transactionTable.store
            &&    RetailTransactionSalesTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionSalesTrans.terminalId    == transactionTable.terminal
            &&    RetailTransactionSalesTrans.transactionId == transactionTable.transactionId).recId)
    {
        transactionSalesTrans.enabled(true);
    }

    if ((select firstonly recId from RetailTransactionPaymentTrans
            where RetailTransactionPaymentTrans.store         == transactionTable.store
            &&    RetailTransactionPaymentTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionPaymentTrans.terminal      == transactionTable.terminal
            &&    RetailTransactionPaymentTrans.transactionId == transactionTable.transactionId).recId)
    {
        transactionPaymentTrans.enabled(true);
    }

    if ((select firstonly recId from RetailTransactionDiscountTrans
            where RetailTransactionDiscountTrans.StoreId       == transactionTable.store
            &&    RetailTransactionDiscountTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionDiscountTrans.TerminalId    == transactionTable.terminal
            &&    RetailTransactionDiscountTrans.TransactionId == transactionTable.transactionId).recId)
    {
        transactionDiscountTrans.enabled(true);
    }

    if ((select firstonly recId from RetailTransactionIncomeExpenseTrans
            where RetailTransactionIncomeExpenseTrans.store         == transactionTable.store
            &&    RetailTransactionIncomeExpenseTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionIncomeExpenseTrans.terminal      == transactionTable.terminal
            &&    RetailTransactionIncomeExpenseTrans.transactionId == transactionTable.transactionId).recId)
    {
        transactionIncomeExpenseTrans.enabled(true);
    }

    if ((select firstonly recId from RetailTransactionInfocodeTrans
            where RetailTransactionInfocodeTrans.store         == transactionTable.store
            &&    RetailTransactionInfocodeTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionInfocodeTrans.terminal      == transactionTable.terminal
            &&    RetailTransactionInfocodeTrans.transactionId == transactionTable.transactionId).recId)
    {
        transactionInfocodeTrans.enabled(true);
    }

    if ((select firstonly recId from RetailTransactionTenderDeclarationTrans
            where RetailTransactionTenderDeclarationTrans.store         == transactionTable.store
            &&    RetailTransactionTenderDeclarationTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionTenderDeclarationTrans.terminal      == transactionTable.terminal
            &&    RetailTransactionTenderDeclarationTrans.transactionId == transactionTable.transactionId).recId)
    {
        transactionTenderDeclarationTrans.enabled(true);
    }

    if ((select firstonly recId from RetailTransactionBankedTenderTrans
            where RetailTransactionBankedTenderTrans.Store         == transactionTable.store
            &&    RetailTransactionBankedTenderTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionbankedTenderTrans.Terminal      == transactionTable.terminal
            &&    RetailTransactionBankedTenderTrans.transactionId == transactionTable.transactionId).recId)
    {
        transactionBankedTenderTrans.enabled(true);
    }

    if ((select firstonly recId from RetailTransactionSafeTenderTrans
            where RetailTransactionSafeTenderTrans.Store         == transactionTable.store
            &&    RetailTransactionSafeTenderTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionSafeTenderTrans.Terminal      == transactionTable.terminal
            &&    RetailTransactionSafeTenderTrans.transactionId == transactionTable.transactionId).recId)
    {
        transactionSafeTenderTrans.enabled(true);
    }

    // LIS.GV.11.12.2006 Start
    if ((select firstonly recId from RetailTransactionLoyaltyRewardPointTrans
            where RetailTransactionLoyaltyRewardPointTrans.storeId       == transactiontable.store
            &&    RetailTransactionLoyaltyRewardPointTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionLoyaltyRewardPointTrans.terminalId    == transactionTable.terminal
            &&    RetailTransactionLoyaltyRewardPointTrans.transactionId == transactionTable.transactionId).recId)
    {
        transactionLoyaltyRewardPointTrans.enabled(true);
    }
    // LIS.GV.11.12.2006 End

    if ((select firstonly recId from RetailTransactionOrderInvoiceTrans
            where RetailTransactionOrderInvoiceTrans.storeId       == transactiontable.store
            &&    RetailTransactionOrderInvoiceTrans.Channel       == transactionTable.Channel
            &&    RetailTransactionOrderInvoiceTrans.terminalId    == transactionTable.terminal
            &&    RetailTransactionOrderInvoiceTrans.transactionId == transactionTable.transactionId).recId)
    {
        transactionOrderInvoiceTrans.enabled(true);
    }

    if (RetailLogTransactionReference::existsByRef(
        transactionTable.Channel,
        transactionTable.store,
        transactionTable.terminal,
        transactionTable.transactionId))
    {
        transactionRetailLog.enabled(true);
    }

    if ((select firstonly TransactionId from RetailTransactionSupplementaryInvoice
            where RetailTransactionSupplementaryInvoice.Store           == transactionTable.store
            &&    RetailTransactionSupplementaryInvoice.Channel         == transactionTable.Channel
            &&    RetailTransactionSupplementaryInvoice.Terminal        == transactionTable.terminal
            &&    RetailTransactionSupplementaryInvoice.TransactionId   == transactionTable.transactionId).TransactionId)
    {
        transactionSupplementaryInvoice.enabled(true);
    }

    super();
}

void clicked()
{
    if (transactionTable.entryStatus == RetailEntryStatus::Posted)
    {
        throw error("@RET4203");
    }

    if (transactionTable.wrongShift == NoYes::Yes)
    {
        if (box::yesNo(strfmt("@RET4204"), DialogButton::No, "@RET4205") == DialogButton::Yes)
        {
            transactionTable.wrongShift = NoYes::No;
            transactionTable.update();
        }
    }
    else
    {
        if (box::yesNo(strfmt("@RET4206"), DialogButton::No, "@RET4205") == DialogButton::Yes)
        {
            transactionTable.wrongShift = NoYes::Yes;
            transactionTable.update();
        }
    }

    transactionTable_ds.reread();
}

/// <summary>
/// Triggers parameters dialog.
/// </summary>
public void clicked()
{
    super();
    Args args = new Args(formstr(RetailReportParameterSlider));

    args.parmObject(params);

    MenuFunction menuFunction =
            new MenuFunction(menuitemDisplayStr(RetailReportParameterSlider), MenuItemType::Display);

    FormRun formRun = menuFunction.create(args);
    formRun.init();
    formRun.run();
    formRun.wait();

    if (formRun.closedOk())
    {
        params = args.parmObject();
        transactionTable_ds.executeQuery();
    }
}
