boolean allowEditQty(InventTable _inventTable)
{
    return !_inventTable.hasProductDimensions();
}

/// <summary>
/// Checks if a chosen replacement item has an alternative item setup.
/// </summary>
/// <param name="_inventTable">
/// An InventTable buffer representing the chosen replacement item.
/// </param>
/// <remarks>
/// If an alternative item setup exist warnnings are raised to inform the user the chosen item might be substituted later in the process.
/// </remarks>
public void checkAltItemSetup(InventTable _inventTable)
{
    if (_inventTable.hasActiveAltItemId())
    {
        warning(strFmt("@SYS127943", _inventTable.ItemId));
        warning("@SYS127945");
    }
}

InventDim createInventDimLocal(InventDim  _inventDim)
{
    select inventDimLocal
         where inventDimLocal.InventDimId == _inventDim.InventDimId;

    if (inventDimLocal)
    {
        return inventDimLocal;
    }
    else
    {
        buf2Buf(_inventDim,inventDimLocal);
        inventDimLocal.doInsert();
    }

    return inventDimLocal;
}

void  doApply()
{
    switch (callerRecord.TableId)
    {
        // sales return lines
        case tableNum(SalesLine):
            ReturnReplaceItemRef::createRefReturnFromTmpFrmVirtual(origSalesId,origSalesLineNum,tmpFrmVirtual);
            break;
        // quarantine order
        case tableNum(InventQuarantineOrder):
            ReturnReplaceItemRef::createRefInventFromTmpFrmVirtual(origSalesId,origSalesLineNum,refQuarantineId,tmpFrmVirtual);
            break;
        // Item arrival journal
        case tableNum(WMSJournalTrans):
            ReturnReplaceItemRef::createRefWMSFromTmpFrmVirtual(origSalesId,origSalesLineNum,refWMSJournalId,refWMSJournalLineNum,tmpFrmVirtual);
            break;
        default:
            break;
    }
    inventsum_ds.executeQuery();

    super();
}

InventDimId   findDimFromCombination(InventDimCombination _inventDimCombination, InventDim _inventDim = inventDimAll)
{
    InventDim   inventDimFind;
    InventDim   inventDimCreate;

    inventDimFind.initFromInventDim(_inventDim);
    EcoResProductDimGroupSetup::copyProductDimensionsForItem(_inventDimCombination.ItemId, _inventDimCombination.inventDim(), inventDimFind);

    if (inventDimMap.exists(inventDimFind.xml()))
    {
        inventDimId = inventDimMap.lookup(inventDimFind.xml());
    }
    else
    {
        inventDimCreate = InventDim::findOrCreate(inventDimFind);
        inventDimId     = inventDimCreate.InventDimId;
        inventDimMap.insert(inventDimFind.xml(), inventDimId);
        element.createInventDimLocal(inventDimCreate);
    }

    return inventDimId;
}

void init()
{
    super();

    queryFirst = true;
    queryFirstItem = true;
    origQuery = inventSum_ds.query();

    if (!element.args().record())
    {
        // Form '%1' must be called with caller.
        throw error(strFmt("@SYS84645", element.name()));
    }

    callerRecord = element.args().record();

    element.inventDimSetupObject().initFromCaller();

    element.initFromReplaceItemRef();

    element.updateDesign(InventDimFormDesignUpdate::Init);

    inventDimId = InventDim::inventDimIdBlank();

    inventDimMap            = new Map(Types::String, Types::String);

    inventDimLocal.setTmp();

    inventAvailabilityFormCache = InventAvailabilityFormCache::construct();
}

void initDataScourceQuery(FormDataSource _inventTable_ds)
{
    Query    query;

    if (_inventTable_ds.queryRun())
    {
        query = _inventTable_ds.queryRun().query();
    }
    else
    {
        query = _inventTable_ds.query();
    }
    query.dataSourceTable(tableNum(InventDim)).addSortIndex(indexNum(InventDim, DimIdIdx));
    query.dataSourceTable(tableNum(InventDim)).indexIsHint(true);
    query.dataSourceTable(tableNum(InventSum)).addSortIndex(indexNum(InventSum, ItemDimIdx));
    query.dataSourceTable(tableNum(InventSum)).indexIsHint(true);
}

void initFromReplaceItemRef()
{
    ReturnReplaceItemRef    replaceItemRef;
    InventTable             inventTableFind;
    WMSJournalTrans         wmsJournalTrans;
    InventQuarantineOrder   quarantineOrder;
    SalesLine               salesLine;

    switch (callerRecord.TableId)
    {
        // return lines
        case tableNum(SalesLine):
            salesLine               = callerRecord;
            if (salesLine.ReturnStatus != ReturnStatusLine::None)
            {
                refSalesId              = salesLine.SalesId;
                refSalesLineNum         = salesLine.LineNum;
            }
            break;

        // quarantine order
        case tableNum(InventQuarantineOrder):
            quarantineOrder         = callerRecord;
            if (quarantineOrder.TransType == InventTransType::Sales)
            {
                refQuarantineId         = quarantineOrder.QuarantineId;
                salesLine               = SalesLine::findInventTransId(quarantineOrder.InventTransIdRef);
            }
            break;

        // item arrival journal
        case tableNum(WMSJournalTrans):
            wmsJournalTrans         = callerRecord;
            if (wmsJournalTrans.InventTransType == InventTransType::Sales && wmsJournalTrans.ReturnItemNum != '')
            {
                refWMSJournalId         = wmsJournalTrans.JournalId;
                refWMSJournalLineNum    = wmsJournalTrans.LineNum;
                salesLine               = SalesLine::findInventTransId(wmsJournalTrans.InventTransId);
            }

            if (wmsJournalTrans.InventTransType == InventTransType::QuarantineOrder && wmsJournalTrans.ReturnItemNum != '')
            {
                refWMSJournalId         = wmsJournalTrans.JournalId;
                refWMSJournalLineNum    = wmsJournalTrans.LineNum;
                quarantineOrder         = InventQuarantineOrder::find(wmsJournalTrans.InventTransRefId);

                salesLine               = SalesLine::findInventTransId(quarantineOrder.InventTransIdRef);
            }
            break;

        default:
            break;
    }

    if (salesLine.RecId == 0)
    {
        // Return line not found.
        throw error("@SYS118112");
    }
    origSalesId             = salesLine.SalesId;
    origSalesLineNum        = salesLine.LineNum;

    while select ItemId, ReturnReplaceQty, InventDimId, PdsCWReplaceQty from replaceItemRef
        where replaceItemRef.OrigSalesId        == origSalesId
           && replaceItemRef.OrigSalesLineNum   == origSalesLineNum
    {
        tmpFrmVirtual.clear();
        tmpFrmVirtual.initValue();
        inventTableFind = InventTable::find(replaceItemRef.ItemId);

        if (inventTableFind.hasProductDimensions())
        {
            tmpFrmVirtual.TableNum      = tableNum(InventDimCombination);
        }
        else
        {
            tmpFrmVirtual.TableNum      = tableNum(InventSum);
        }
        tmpFrmVirtual.ItemId        = replaceItemRef.ItemId;
        tmpFrmVirtual.Qty           = replaceItemRef.ReturnReplaceQty;

        tmpFrmVirtual.PdsCWQty      = replaceItemRef.PdsCWReplaceQty;

        tmpFrmVirtual.InventDimId   = replaceItemRef.InventDimId;
        tmpFrmVirtual.insert();
    }
    element.updateQtyAllocated();
}

void initParmDefault()
{
}

void initQueryRangeValue()
{
    SalesLine               salesLine;
    WMSJournalTrans         journalLine;
    InventQuarantineOrder   quarantineOrder;

    ItemId          replaceItemId, altItemId;
    Qty             replaceQty;

    PdsCWInventQty  cwReplaceQty;

    queryItemId.value(SysQuery::valueEmptyString());
    queryAltItemId.value(SysQuery::valueEmptyString());

    switch (element.args().dataset())
    {
        case tableNum(SalesLine):
            salesLine       = element.args().record();
            replaceItemId   = salesLine.ItemId;
            replaceQty      = -salesLine.ExpectedRetQty;

            cwReplaceQty    = -salesLine.PdsCWExpectedRetQty;
            break;

        case tableNum(WMSJournalTrans):
            journalLine     = element.args().record();
            replaceItemId   = journalLine.ItemId;
            replaceQty      = journalLine.Qty;

            cwReplaceQty    = journalLine.PdsCWQty;
            break;

        case tableNum(InventQuarantineOrder):
            quarantineOrder = element.args().record();
            replaceItemId   = quarantineOrder.ItemId;
            replaceQty      = quarantineOrder.Qty;

            cwReplaceQty    = quarantineOrder.PdsCWQty;
            break;

        default:
            break;
    }
    // replace exact Item
    queryItemId.value(replaceItemId);

    // replace with alternative Item
    altItemId = InventTable::find(replaceItemId).AltItemId;
    queryAltItemId.value(SysQuery::value(altItemId));

    // set qty to replace
    qty2allocate.realValue(replaceQty);

    pdsCWQty2Allocate.realValue(cwReplaceQty);
}

