}

public SalesQty parmReturnQty(boolean _set,InventTable _inventTable,SalesQty _qty, InventSum _inventSum)
{
    SalesQty    qty;
    InventDim   replacementItemInventDim = _inventSum.joinChild();

    replacementItemInventDim = InventDim::findOrCreate(replacementItemInventDim);

    select tmpFrmVirtual
        where   tmpFrmVirtual.TableNum      == tableNum(InventSum) &&
                tmpFrmVirtual.ItemId        == _inventTable.ItemId &&
                tmpFrmVirtual.InventDimId   == replacementItemInventDim.InventDimId;

    if (tmpFrmVirtual)
    {
        qty = tmpFrmVirtual.Qty;
    }
    else
    {
        tmpFrmVirtual.TableNum      = tableNum(InventSum);
        tmpFrmVirtual.ItemId        = _inventTable.ItemId;
        tmpFrmVirtual.InventDimId   = replacementItemInventDim.InventDimId;
        tmpFrmVirtual.write();
    }

    if (_set && _inventTable.inventItemSalesSetup(replacementItemInventDim.InventDimId).checkNotStopped(false))
    {
        tmpFrmVirtual.Qty = (_qty) ? _qty : 0;

        tmpFrmVirtual.TableNum      = tableNum(InventSum);
        tmpFrmVirtual.ItemId        = _inventTable.ItemId;
        tmpFrmVirtual.InventDimId   = replacementItemInventDim.InventDimId;
        tmpFrmVirtual.write();

        qty = tmpFrmVirtual.Qty;

        // update design elements
        element.updateQtyAllocated();
    }

    return qty;
}

/// <summary>
/// Returns the current available physical as a catch weight qty
/// </summary>
/// <param name="_inventDim">Specific InventDim record</param>
/// <param name="_inventDimCombination">Specific InventDimCombination</param>
/// <returns>Returns available physical catch weight qty</returns>
public PdsCWInventQty pdsAvailPhysical(
    InventDim               _inventDim,
    InventDimCombination    _inventDimCombination)
{
    InventDim           inventDimFind;
    InventDimParm       inventDimParmLocal;

    inventDimFind.initFromInventDim(_inventDim);
    EcoResProductDimGroupSetup::copyProductDimensionsForItem(_inventDimCombination.ItemId, _inventDimCombination.inventDim(), inventDimFind);
    inventDimParmLocal = inventDimFormSetup.parmDimParmVisibleGrid().data();
    inventDimParmLocal.setActiveProductDim(EcoResProductDimGroupSetup::newItemId(_inventDimCombination.ItemId));

    return InventSum::findSumQty(inventDimCombination.ItemId, inventDimFind,
        inventDimParm).PdsCWAvailPhysical;
}

/// <summary>
/// Returns the catch weight qty of the return line
/// </summary>
/// <param name="_set">Set value to field</param>
/// <param name="_itemId">Current Item</param>
/// <param name="_qty">Catch weight qty</param>
/// <param name="_buffer">Current record</param>
/// <returns>returns amount of return</returns>
public PdsCWInventQty pdsCWReturnQty(
    boolean         _set,
    InventTable     _inventTable,
    PdsCWInventQty  _qty,
    Common          _buffer)
{
    PdsCWInventQty          qty;
    InventDimId             dimId;

    InventDimCombination    localDimCombination;
    InventSum               localInventSum;

    switch (_buffer.TableId)
    {
        case tableNum(InventDimCombination):
            localDimCombination = _buffer;
            dimId = localDimCombination.InventDimId;
            break;

        case tableNum(InventSum):
            localInventSum = _buffer;
            InventDim   replacementItemInventDim = localInventSum.joinChild();
            dimId = InventDim::findOrCreate(replacementItemInventDim).inventDimId;
            break;
    }

    select tmpFrmVirtual
        where  tmpFrmVirtual.TableNum       == _buffer.TableId
            && tmpFrmVirtual.ItemId         == _inventTable.itemId
            && tmpFrmVirtual.InventDimId    == dimId;

    if (tmpFrmVirtual)
    {
        qty = tmpFrmVirtual.PdsCWQty;
    }
    else
    {
        tmpFrmVirtual.TableNum      = _buffer.TableId;
        tmpFrmVirtual.ItemId        = _inventTable.itemId;
        tmpFrmVirtual.InventDimId   = dimId;
        tmpFrmVirtual.write();
    }

    if (_set && _inventTable.inventItemSalesSetup(dimId).checkNotStopped(false))
    {
        tmpFrmVirtual.PdsCWQty      = _qty;
        tmpFrmVirtual.Qty           = PdsCatchWeight::inventQty(_inventTable.itemId, _qty);
        tmpFrmVirtual.TableNum      = _buffer.TableId;
        tmpFrmVirtual.ItemId        = _inventTable.itemId;
        tmpFrmVirtual.InventDimId   = dimId;
        tmpFrmVirtual.write();

        qty = tmpFrmVirtual.PdsCWQty;

        // update design elements
        element.updateQtyAllocated();
    }
    return qty;
}

/// <summary>
/// Method sets the edit status of the catch weight fields
/// </summary>
/// <param name="_buffer">Current record source</param>
/// <param name="_allowEdit">Allow CW fields to be edited</param>
public void pdsEnableCWFields(Common _buffer, boolean _allowEdit)
{
    boolean         cwItem;
    FormControl     qtyField;
    FormControl     cwQtyField;
    FormDataSource  ds = FormDataUtil::getFormDataSource(_buffer);

    switch (ds.name())
    {
        case inventTableItem_ds.name():
            cwItem      = PdsGlobal::pdsIsCWItem(inventTableItem.ItemId);
            qtyField    = salesQty;
            cwQtyField  = pdsCWQty;
            break;

        case inventTableAlt_ds.name():
            cwItem      = PdsGlobal::pdsIsCWItem(inventTableAlt.ItemId);
            qtyField    = alternativeItems_Qty;
            cwQtyField  = alternativeItems_PdsCWQty;
            break;

        case inventTableAll_ds.name():
            cwItem      = PdsGlobal::pdsIsCWItem(inventTableAll.ItemId);
            qtyField    = allItems_Qty;
            cwQtyField  = allItems_PdsCWQty;
            break;

        case inventDimCombination_ds.name():
            cwItem      = PdsGlobal::pdsIsCWItem(inventDimCombination.ItemId);
            qtyField    = salesQtyCombinations;
            cwQtyField  = pdsCWSalesQtyCombinations;
            break;

        case inventDimCombinationAlt_ds.name():
            cwItem      = PdsGlobal::pdsIsCWItem(inventDimCombinationAlt.ItemId);
            qtyField    = altItemsCombinations_Qty;
            cwQtyField  = pdsCWAltItemsCombinations_Qty;
            break;

        case inventDimCombinationAll_ds.name():
            cwItem      = PdsGlobal::pdsIsCWItem(inventDimCombinationAll.ItemId);
            qtyField    = allItemsCombinations_Qty;
            cwQtyField  = pdsCWAllItemsCombinations_Qty;
            break;
    }

    if (qtyField)
    {
        qtyField.allowEdit(_allowEdit && !cwItem);
        cwQtyField.allowEdit(_allowEdit && cwItem);
    }
}

InventQtyAvailPhysical qtyAvailPhysical(InventDimCombination _inventDimCombination, InventDim _inventDim)
{
    InventDim           inventDimFind;
    InventDimParm       inventDimParmLocal;

    inventDimFind.initFromInventDim(_inventDim);
    EcoResProductDimGroupSetup::copyProductDimensionsForItem(_inventDimCombination.ItemId, _inventDimCombination.inventDim(), inventDimFind);
    inventDimParmLocal = inventDimFormSetup.parmDimParmVisibleGrid().data();
    inventDimParmLocal.setActiveProductDim(EcoResProductDimGroupSetup::newItemId(_inventDimCombination.ItemId));
    return InventOnHandQty::newItemDim(_inventDimCombination.ItemId, inventDimFind, inventDimParmLocal).availPhysical();
}

