void resetTmpFrmVirtual()
{
    ttsbegin;

    //delete old settings
    while select tmpFrmVirtual
            where tmpFrmVirtual.Qty
    {
        tmpFrmVirtual.delete();
    }
    ttscommit;
}

void run()
{
    super();

    salesQty.setFocus();

    // init query for identical items
    element.inventDimSetupObject().initQuery(inventTableItem_ds);
    element.inventDimSetupObject().buildQuery();

    // init query for alternative items
    element.inventDimSetupObject().initQuery(inventTableAlt_ds);
    element.inventDimSetupObject().buildQuery();

    // init query for all items
    element.inventDimSetupObject().initQuery(inventTableAll_ds);
    element.inventDimSetupObject().buildQuery();
}

void updateDesign(InventDimFormDesignUpdate mode)
{
    switch (mode)
    {
        case InventDimFormDesignUpdate::Init,
             InventDimFormDesignUpdate::LinkActive  :
            element.inventDimSetupObject().initFromCaller();
            inventDimParm = inventDimFormSetup.parmDimParmVisibleGrid().data();

        case InventDimFormDesignUpdate::Active      :
            element.inventDimSetupObject().formActiveSetup();
            element.inventDimSetupObject().formSetControls(true,true);
            break;

        default : throw error(strFmt("@SYS54195",funcName()));
    }
}

public void updateQtyAllocated()
{
    boolean enableApply = true;

    // update qty allocated

    select sum(Qty), sum(PdsCWQty)
                from tmpFrmVirtual
        where  tmpFrmVirtual.TableNum == tableNum(InventSum)
            || tmpFrmVirtual.TableNum == tableNum(InventDimCombination);

    qtyAllocated.realValue(tmpFrmVirtual.Qty);

    pdsCWQtyAllocated.realValue(tmpFrmVirtual.PdsCWQty);

    // enable Apply button only if at least one item is selected as a replacement
    enableApply = (qtyAllocated.realValue() > 0);
    buttonOK.enabled(enableApply);
}

