void executeQuery()
{
    element.initDataScourceQuery(inventTableItem_ds);

    if (!queryFirstItem)
    {
        inventAvailabilityFormCache.refreshCache();

        super();
    }

    if (#PdsCatchWeightEnabled)
    {
        element.pdsEnableCWFields(inventTableItem, true);
    }

    queryFirstItem = false;
}

public int active()
{
    int             ret;

    ret = super();

    inventTableItemFind = InventTable::find(inventTableItem.ItemId);

    if (#PdsCatchWeightEnabled)
    {
        element.pdsEnableCWFields(inventTableItem, element.allowEditQty(inventTableItemFind));
    }
    else
    {
        salesQty.allowEdit(element.allowEditQty(inventTableItemFind));
    }

    return ret;
}

