                        _set,
                        _inventTable,
                        _qty,
                        inventSumAlt);
}

edit SalesQty  salesQty(boolean _set, InventDimCombination  _inventDimCombination, PurchQty _qty)
{
    SalesQty    qty;

    inventDimId         = element.findDimFromCombination(_inventDimCombination, inventDimItem);
    inventDimParm       = inventDimFormSetup.parmDimParmVisibleGrid().data();
    inventDimParm.setActiveProductDim(EcoResProductDimGroupSetup::newItemId(_inventDimCombination.ItemId));

    inventDimCriteria   = InventDim::find(inventDimId);

    select tmpFrmVirtual
        where   tmpFrmVirtual.TableNum      == tableNum(InventDimCombination)   &&
                tmpFrmVirtual.ItemId        == _inventDimCombination.ItemId     &&
                tmpFrmVirtual.InventDimId   == inventDimId;

    if (_set && element.checkNotStopped(_inventDimCombination.inventTable(), inventDimId))
    {
        tmpFrmVirtual.Qty = (_qty) ? _qty : 0;

        tmpFrmVirtual.TableNum      = tableNum(InventDimCombination);
        tmpFrmVirtual.ItemId        = _inventDimCombination.ItemId;
        tmpFrmVirtual.InventDimId   = inventDimId;
        tmpFrmVirtual.write();

        // update design elements
        element.updateQtyAllocated();
    }

    select sum(Qty) from tmpFrmVirtual
        where   tmpFrmVirtual.TableNum      == _inventDimCombination.TableId &&
                tmpFrmVirtual.ItemId        == _inventDimCombination.ItemId
    #InventDimJoin(tmpFrmVirtual.inventDimId,inventDimLocal,inventDimCriteria,inventDimParm);

    qty = tmpFrmVirtual.Qty;

    return qty;
}

display InventQtyAvailPhysical availPhysical(InventDimCombination _inventDimCombination)
{
    return element.qtyAvailPhysical(_inventDimCombination, inventDimItem);
}

/// <summary>
/// Sets the catch weight qty of the return line
/// </summary>
/// <param name="_set">Set value to field</param>
/// <param name="_inventDimCombination">Specific InventDimCombination record</param>
/// <param name="_qty">Catch weight field qty</param>
/// <returns>Returns current catch weight value</returns>
public edit PdsCWInventQty pdsCWQty(
    boolean                 _set,
    InventDimCombination    _inventDimCombination,
    PdsCWInventQty          _qty)
{
    PdsCWInventQty      qty;

    inventDimId         = element.findDimFromCombination(_inventDimCombination, inventDimItem);
    inventDimParm       = inventDimFormSetup.parmDimParmVisibleGrid().data();
    inventDimParm.setActiveProductDim(EcoResProductDimGroupSetup::newItemId(_inventDimCombination.ItemId));

    inventDimCriteria   = InventDim::find(inventDimId);

    select tmpFrmVirtual
        where  tmpFrmVirtual.TableNum      == tableNum(InventDimCombination)
            && tmpFrmVirtual.ItemId        == _inventDimCombination.ItemId
            && tmpFrmVirtual.InventDimId   == inventDimId;

    if (_set && element.checkNotStopped(_inventDimCombination.inventTable(), inventDimId))
    {
        tmpFrmVirtual.PdsCWQty      = _qty;
        tmpFrmVirtual.Qty           = PdsCatchWeight::inventQty(
                _inventDimCombination.ItemId, _qty);

        tmpFrmVirtual.TableNum      = tableNum(InventDimCombination);
        tmpFrmVirtual.ItemId        = _inventDimCombination.ItemId;
        tmpFrmVirtual.InventDimId   = inventDimId;
        tmpFrmVirtual.write();

        // update design elements
        element.updateQtyAllocated();
    }

    select sum(PdsCWQty) from tmpFrmVirtual
        where  tmpFrmVirtual.TableNum      == _inventDimCombination.TableId
            && tmpFrmVirtual.ItemId        == _inventDimCombination.ItemId
    #InventDimJoin(
        tmpFrmVirtual.inventDimId,
        inventDimLocal,
        inventDimCriteria,inventDimParm);

    qty = tmpFrmVirtual.PdsCWQty;

    return qty;
}

