boolean modified()
{
    boolean     ret;
    SalesQty    tmpSalesQty;

    ret         = super();
    tmpSalesQty = element.quantityModified(inventTableItemFind, this.realValue(), inventDimItem.InventDimId);

    if (tmpSalesQty != this.realValue())
    {
        inventTableItem_ds.salesQty(true, inventTableItem, tmpSalesQty);
    }

    return ret;
}

