/// <summary>
/// Validates if the disposition code is valid.
/// </summary>
/// <returns>true if valid; otherwise, false.</returns>
[Replaceable]
protected boolean isDispositionCodeValid()
{
    return salesLine.ReturnDispositionCodeId
        && salesLine.ReturnDispositionCodeId != salesLine.orig().ReturnDispositionCodeId
        && ReturnDispositionCode::find(salesLine.ReturnDispositionCodeId).DispositionAction != DispositionAction::CreditOnly;
}

/// <summary>
/// Customizes the options used to populate the Office Menu with the return order entities.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();

        DictDataEntity dataEntity = new DictDataEntity(tablename2Id(dataEntityOptions.dataEntityName()));

        if (dataEntity.formRef() != menuItemDisplayStr(ReturnTableDetails))
        {
            dataEntityOptionsIterator.delete();
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }

    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(dataentityviewstr(ReturnOrderLineV2Entity), ExportReturnOrderHeaderAndLinesToExcelOptionId);
    exportOption.displayName(strfmt("@SYS76681", "@SCM:ReturnOrderHeaderAndLines", '', curExt()));
    exportOptions.addEnd(exportOption);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportReturnOrderHeaderAndLinesToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(dataentityviewstr(ReturnOrderHeaderV2Entity));

            var returnOrderHeaderEntityInfo = context.findEntityByName(dataentityviewstr(ReturnOrderHeaderV2Entity));

            if (returnOrderHeaderEntityInfo)
            {
                var returnOrderHeaderFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(ReturnOrderHeaderV2Entity));
                var returnOrderHeaderIdFilter = returnOrderHeaderFilterBuilder.areEqual(fieldStr(ReturnOrderHeaderV2Entity, ReturnOrderNumber), SalesTable.SalesId);

                returnOrderHeaderEntityInfo.filter(returnOrderHeaderFilterBuilder.and(returnOrderHeaderFilterBuilder.companyFilter(), returnOrderHeaderIdFilter));
            }

            var returnOrderLineEntityInfo = context.addEntityDefault(dataentityviewstr(ReturnOrderLineV2Entity));
            var returnOrderLineFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(ReturnOrderLineV2Entity));
            var returnOrderLineIdFilter = returnOrderLineFilterBuilder.areEqual(fieldStr(ReturnOrderLineV2Entity, ReturnOrderNumber), SalesTable.SalesId);
            returnOrderLineEntityInfo.filter(returnOrderLineFilterBuilder.and(returnOrderLineFilterBuilder.companyFilter(), returnOrderLineIdFilter));
            break;
    }

    return context;
}

public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);

    if (_menuItem.dataEntityName() == dataentityviewstr(ReturnOrderHeaderEntity))
    {
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(ReturnOrderLineEntity));

        filtersToApply.insert(dataentityviewstr(ReturnOrderLineEntity), lineFilterBuilder.areEqual(fieldStr(ReturnOrderLineEntity, ReturnOrderNumber), SalesTable.SalesId));
    }

    if (_menuItem.dataEntityName() == dataentityviewstr(ReturnOrderHeaderV2Entity))
    {
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(ReturnOrderLineV2Entity));

        filtersToApply.insert(dataentityviewstr(ReturnOrderLineV2Entity), lineFilterBuilder.areEqual(fieldStr(ReturnOrderLineV2Entity, ReturnOrderNumber), SalesTable.SalesId));
    }

    return filtersToApply;
}

public void updateTemplateSettings(OfficeTemplateExportMenuItem _menuItem, Microsoft.Dynamics.Platform.Integration.Office.SettingsManager _settingsManager)
{
    // No implementation details for this since we don't need to update the template settings
}

public boolean canClose()
{
    boolean  ret;

    ret = super();

    if (element.closedCancel() && ret)
    {
        ret = salesTableForm.canClose(salesTable);
    }

    if (mcrCallCenterEnabled)
    {
        // Don't allow open returns.
        ret = ret && element.mcrCheckOpenOrder();
    }

    return ret;
}

public void close()
{
    FormDataSource formDataSource;
    super();

    salesTableForm.isAdvanced(advanced);
    xSysLastValue::saveLast(salesTableForm);

    if (element.args() && element.args().caller() && element.args().caller().name() == formStr(ReturnTableListPage))
    {
        formDataSource = FormDataUtil::getFormDataSource(element.args().record());
        if (formDataSource)
        {
            formDataSource.research(true);
        }
    }
}

public Common docCursor()
{
    Common docCursor;

    docCursor = super();

    if (docCursor.TableId == tableNum(InventDim))
    {
        docCursor = salesLine;
    }

    return docCursor;
}

/// <summary>
///     Updates the record buffers displayed in the form.
/// </summary>
/// <param name="_lineRefreshNeeded">
///     A boolean type specifying whether salesLine_ds should be reread or researched.
///     If true the salesLine_ds is reread, otherwise, salesLine_ds is researched.
/// </param>
void doRefresh(boolean _lineRefreshNeeded = false)
{
    salesTable_ds.reread();
    salesTable_ds.refresh();

    if (_lineRefreshNeeded)
    {
        salesLine_ds.reread();
        salesLine_ds.refresh();
    }
    else
    {
        salesLine_ds.research();
    }
    if (mcrCallCenterEnabled)
    {
        MCRReturnSalesTable.data(SalesTable.mcrReturnSalesTable());
        MCRReturnSalesTable_ds.refresh();
    }
}

/// <summary>
/// Enables available line actions in connection with Agreements
/// </summary>
/// <remarks>
///    Invoked whenever a link to an agreement is created or removed for the return line.
/// </remarks>
public void enableLineAgreementActions()
{
    returnTableButtonStateProvider.setDatasources(salesTable, salesLine);
    // Agreement buttons (line)
    createLink.enabled(returnTableButtonStateProvider.checkRuleCreateLink());
    removeLink.enabled(returnTableButtonStateProvider.checkRuleRemoveLink());
    salesAgreementLine.enabled(returnTableButtonStateProvider.checkRuleSalesAgreementLine());
}

void gotoLines()
{
    salesLineGrid.setFocus();
}

public void init()
{
    SalesTable salesTableSO, salesTableRO;

    if (element.args().menuItemName() == menuitemDisplayStr(MCRCreateRMA))
    {
        // Selected sales order (salesTable)
        salesTableSO = element.args().record();

        if (salesTableSO.RecId)
        {
            salesTableRO = this.initReturnOrderFromSalesTable(salesTableSO);

            if (salesTableRO.RecId)
            {
                // Initiate datasource with created return order
                element.args().record(salesTableRO);
            }
        }
    }

    returnTableButtonStateProvider = new ReturnTableButtonStateProvider();

    if (element.args() && !TradeFormHelper::isCalledFromForm(element.args(), formStr(ReturnTableListPage)))
    {
        element.args().copyCallerQuery(CopyCallerQuery::No);
    }
    if (#PdsCatchWeightEnabled)
    {
        formCtrl = PdsCWFormCtrl_ReturnTable::newFromForm(element);
        formCtrl.initPre();
    }

    // <GIN>
    if (TaxParameters::isExciseEnable_IN())
    {
        isSalesLine_INEnabled = SysExtensionSerializerExtensionMap::isExtensionEnabled(tableNum(SalesLine_IN));
    }
    // </GIN>

    super();

    this.showMessages();

    this.dataSource().init();

    salesTableForm = SalesTableForm::construct(SalesTableFormId::ReturnOrder, element.args().record());

    salesTableForm.parmFormDataSource(salesTable_ds);
    salesTableForm.parmArgs(this.args());

    salesTableForm.parmReturnStatus(ReturnStatusHeader::Created);

    if (formCtrl)
    {
        formCtrl.initPost();
    }

    element.updateDesign(InventDimFormDesignUpdate::Init);

    // Default filter on RMS Status is "All"
    filterRMAStatus.selection(ReturnStatusFilter::All);
    element.setFilterAccess();

    salesTable_ds.query(salesTableForm.querySalesTable(salesTable_ds.query()));
    salesLine_ds.query(salesTableForm.querySalesLine(salesLine_ds.query()));

    // Needed to check validity of address
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidFrom));
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, ValidTo));
    logisticsPostalAddress_ds.addFieldToSelectionList(fieldNum(LogisticsPostalAddress, CountryRegionId));

    if (isConfigurationkeyEnabled(configurationKeyNum(MCRCallCenter)))
    {
        mcrCallCenterEnabled = true;
    }

    TradeFormsInstrumentationActivities::instrumentTradeFormQueryFilters(formStr(ReturnTable), salesTable_ds.query());
}

/// <summary>
/// Create return order from a sales order.
/// </summary>
/// <param name="_salesTable">
/// Source original sales order record.
/// </param>
/// <returns>
/// Return salesTable of return order.
/// </returns>
/// <remarks>
/// Generate RMA only opens the return order form.
/// </remarks>
public SalesTable initReturnOrderFromSalesTable(SalesTable _salesTable)
{
    SalesLine           salesLineSource, returnLine;
    SalesTable          returnTable;
    MCRReturnSalesTable mcrReturnSalesTableExtension;
    CustInvoiceTrans    custInvoiceTrans;
    SalesQty            totalReturnQty = 0;
    PdsCWInventQty      cwTotalReturnQty = 0;
    CreditCardAuthTrans chargeAuthTrans;
    CreditCardAuthTrans refundAuthTrans;

    if ((!_salesTable) || (_salesTable.SalesType == SalesType::ReturnItem))
    {
        throw error('@Retail:CantCreateReturnOrder');
    }

    try
    {
        ttsbegin;
        returnTable.initValue(SalesType::ReturnItem);

        returnTable.initReturnFromSalesTable(_salesTable);

        SalesCalcAvailableDlvDates::initDateFields(returnTable);

        returnTable.DeliveryDateControlType = SalesParameters::find().DeliveryDateControlType;

        if (SalesCopyingSetReturnAddressFromInventLocationFlight::instance().isEnabled())
        {
            returnTable.InventLocationId        = _salesTable.InventLocationId;
            returnTable.InventSiteId            = _salesTable.InventSiteId;
            returnTable.setReturnAddressFromInventLocationId(returnTable.InventLocationId);
        }
        else
        {
            returnTable.setReturnAddressFromInventLocationId(returnTable.InventLocationId);
            returnTable.InventLocationId        = _salesTable.InventLocationId;
            returnTable.InventSiteId            = _salesTable.InventSiteId;
        }

        // Inserting retailchannel field of sales table.
        returnTable.RetailChannelTable      = _salesTable.RetailChannelTable;

        if (_salesTable.CreditCardCustRefId != 0)
        {
            // Link Original Sales Order Credit Card to the Return order
            returnTable.CreditCardCustRefId = _salesTable.CreditCardCustRefId;

            // Copy the charge auth trans details to be refunded.
            while select chargeAuthTrans
                where chargeAuthTrans.SalesId == _salesTable.SalesId &&
                      (
                       chargeAuthTrans.ApprovalType == CreditCardOperation::CaptureAfterAuthorize ||
                       chargeAuthTrans.ApprovalType == CreditCardOperation::AuthorizeAndCapture
                      ) &&
                      chargeAuthTrans.ProcessorStatus == CreditCardProcessorStatus::Approved
            {
                refundAuthTrans = chargeAuthTrans.data();
                refundAuthTrans.RefundStatus = CreditCardRefundStatus::Marked;
                refundAuthTrans.SalesId = returnTable.SalesId;
                refundAuthTrans.insert();
            }
        }

        if (returnTable.validateWrite())
        {
            returnTable.insert();
        }
        else
        {
            throw error("@SYS18722");
        }

        if (_salesTable.isRetailSale() && RetailParameters::isRetailAutoChargesEnabledAndInUse())
        {
            RetailRefundChargeLines::CreateRefundChargeLinesForReturnOrder(returnTable, _salesTable);
        }

        // Set MCRReturnSalesTable values
        mcrOrderParameters = MCROrderParameters::find();

        mcrReturnSalesTableExtension = returnTable.mcrReturnSalesTable();
        mcrReturnSalesTableExtension.ApplyCreditToExchange = mcrOrderParameters.ApplyCreditToExchange;
        mcrReturnSalesTableExtension.AdvanceExchange = mcrOrderParameters.AdvanceExchange;
        mcrReturnSalesTableExtension.AdvanceCredit = mcrOrderParameters.AdvanceCredit;
        mcrReturnSalesTableExtension.OriginalSalesId = _salesTable.SalesId;
        mcrReturnSalesTableExtension.update();

        var orderCreditMessage = MCRCustCreditTable::getOrderCreditNotificationMessageIfExists(_salesTable.SalesId);
        this.addMessage(orderCreditMessage);

        // Create return line for invoiced qty of sales line
        while select * from salesLineSource
            join LedgerDimension from custInvoiceTrans
            where custInvoiceTrans.InventTransId == salesLineSource.InventTransId
            && salesLineSource.SalesId == _salesTable.SalesId
        {
            totalReturnQty   = 0;
            cwTotalReturnQty = 0;

            // Calculate already return quantity against salesline
            [totalReturnQty, cwTotalReturnQty] = SalesLine::pdsRemainReturnLotQtyAllCreditNote(
                                                            salesLineSource.InventTransId,
                                                            salesLineSource.ProjId,
                                                            salesLineSource.SalesUnit);

            if ((totalReturnQty < salesLineSource.invoicedInTotal()) || (cwTotalReturnQty < salesLineSource.pdsCWInvoicedInTotal()))
            {
                returnLine.clear();

                returnLine.initValue(SalesType::ReturnItem);

                // Update with return order header information
                returnLine.initFromSalesTable(returnTable);

                // Copy from sales line
                returnLine.initFromSalesLine(salesLineSource);

                returnLine.LedgerDimension          = custInvoiceTrans.LedgerDimension;
                returnLine.RefReturnInvoiceTrans_W  = custInvoiceTrans.RecId;
                returnLine.InventTransIdReturn      = salesLineSource.InventTransId;

                returnLine.SalesQty    = 0;
                returnLine.QtyOrdered  = 0;
                returnLine.PdsCWQty    = 0;

                returnLine.ExpectedRetQty       = -1 * (salesLineSource.invoicedInTotal() - totalReturnQty); //Available quantity to return
                returnLine.SalesPrice  = ExchangeRateHelper::curPrice2CurPrice(returnLine.SalesPrice, salesLineSource.CurrencyCode, returnLine.CurrencyCode);
                returnLine.LineAmount  = returnLine.returnLineAmount();
                SalesCalcAvailableDlvDates::initDateFields(returnLine);

                returnLine.createLine(true, false, false, false, false, false, _salesTable.Reservation == ItemReservation::Automatic, true
                    , false             // _ignoreSalesTableInventDim
                    , true              // _setLineNum
                    , true              // _searchAgreementLine
                    , null              // _smaServiceOrderTable
                    , 0                 // _oldAgreementLineRecId
                    , salesLineSource.InventTransId); // _mcrExchangeInventRefTransId

                if (_salesTable.isRetailSale() && RetailParameters::isRetailAutoChargesEnabledAndInUse())
                {
                    RetailRefundChargeLines::CreateRefundChargeLinesForReturnLine(returnLine, salesLineSource);
                }

                // If the original sales line was price matched, need to create
                // the offsetting misc. charge so the customer will not get extra money.
                MCRCustCreditTable mcrCustCreditTable = MCRCustCreditTable::findByInventTransID(returnLine.InventTransIdReturn);
                if (mcrCustCreditTable)
                {
                    var messageAfterAddingCharge = mcrCustCreditTable.generateMiscChargeAndGetMessage(returnLine.InventTransId, returnLine.InventTransIdReturn);
                    this.addMessage(messageAfterAddingCharge);
                }
            }
        }
        ttscommit;
    }
    catch
    {
        throw error("@SYS18722");
    }

    return returnTable;
}

private void addMessage(str _message)
{
    if (_message != '')
    {
        infoMessages[messageIndex] = _message;
        messageIndex++;
    }
}

private void showMessages()
{
    int i;
    for (i = 1; infoMessages[i]; i++)
    {
        info(infoMessages[i]);
    }
}

/// <summary>
/// Performs defaulting and validation releated to modifying an invent dimension field.
/// </summary>
/// <param name = "_fieldId">An invent dimension field ID.</param>
public void modifyInventDimField(FieldId _fieldId)
{
    SalesQty salesQty = SalesLine.SalesQty;
    InventQty qtyOrdered = SalesLine.QtyOrdered;

    if (SalesLine.modifyInventDim(inventDim, _fieldId))
    {
        InventDim.data(SalesLine.inventDim());
        InventDim_DS.setCurrent();
    }

    SalesLine.SalesQty = salesQty;
    SalesLine.QtyOrdered = qtyOrdered;
}

