{
    super();
    salesLine.setExpectedReturnQuantityFromCWQuantity(inventDim);
}

/// <summary>
/// Retail Variant ID - Modified.
/// </summary>
public void modified()
{
    super();

    SalesQty cacheSalesQty = salesLine.SalesQty;
    InventQty cacheQtyOrdered = salesLine.QtyOrdered;
    InventSiteId cacheInventSiteId = inventDim.InventSiteId;

    InventDim variantInventDim = InventDimCombination::findVariantId(salesLine.RetailVariantId).inventDim();
    if (variantInventDim)
    {
        Set productDimensionFields = inventDim.copyProductDimensionsAndGetModifiedDimensionFields(variantInventDim, salesLine.ItemId);
        if (productDimensionFields)
        {
            if (salesLine.modifyInventDimSet(inventDim, productDimensionFields, !salesLine.MatchingAgreementLine))
            {
                inventDim.data(salesLine.inventDim());
                inventDim_ds.setCurrent();
            }
        }
    }

    salesLine.SalesQty = cacheSalesQty;
    salesLine.QtyOrdered = cacheQtyOrdered;
    inventDim.InventSiteId = cacheInventSiteId;
}

public void modified()
{
    super();
    salesLine_ds.write();
    return_ReturnDispositionCodeId.enabled(!salesLine.ReturnAllowReservation);
}

public void modified()
{
    if (!salesLine_ds.cursor().RecId)
    {
        salesLine_ds.write();
    }

    super();
    return_ReturnAllowReservation.enabled(salesLine.ReturnStatus == ReturnStatusLine::Awaiting && !salesLine.ReturnDispositionCodeId);

    // Update inventDim with new data which has been set due to modification
    inventDim.data(salesLine.inventDim());
    inventDim_ds.setCurrent();
    inventDim_ds.refresh();
}

public void lookup(FormControl _formControl, str _filterStr)
{
    Set dispositionActions = new Set(Types::Enum);

    dispositionActions.add(DispositionAction::CreditOnly);
    ReturnDispositionCode::lookupDispositionCode(_formControl, _filterStr, dispositionActions);
}

public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret && this.isDispositionCodeValid())
    {
        // The entered value '%1' is not a valid Disposition code.
        ret = checkFailed(strFmt("@SYS121768", salesLine.ReturnDispositionCodeId));
    }

    return ret;
}

/// <summary>
/// Validates if the disposition code is valid.
/// </summary>
/// <returns>true if valid; otherwise, false.</returns>
[Replaceable]
protected boolean isDispositionCodeValid()
{
    return element.isDispositionCodeValid();
}

