/// <summary>
///     Calls the complete logic for the return.
/// </summary>
void clicked()
{
    element.mcrComplete();
}

void clicked()
{
    super();

    // Validate the Return Order values when creating a Replacement Order now that the user can customize them. Disallowed scenarios are:
    // Advance credit = Yes
    // Apply credit = Yes and Advance exchange = BLANK
    if (MCRReturnSalesTable.AdvanceCredit == NoYes::Yes)
    {
        throw Error("@MCR23366");
    }

    if (MCRReturnSalesTable.ApplyCreditToExchange == NoYes::Yes && MCRReturnSalesTable.AdvanceExchange == MCRAdvanceExchange::BLANK)
    {
        throw Error("@MCR23090");
    }

    if (RetailOrdersFeatureControl::isSkipReturnOrderSummaryForReplacementOrdersEnabled())
    {
        // To allow navigating to the replacement order, we set the open return order to null so the order recap isn't triggered.
        openReturnSalesTable = null;
    }

    salesTableForm.returnCreateReplacement();

    SalesLine_ds.executeQuery();
    element.mcrSetHeaderControls();
    MCRReturnSalesTable.data(SalesTable.mcrReturnSalesTable());
    MCRReturnSalesTable_ds.refresh();
}

/// <summary>
///     Allows the return to be opened so the user can modify it.
/// </summary>
void clicked()
{
    if (SalesTable.SalesStatus == SalesStatus::Backorder)
    {
        // Roll back the sales order status.
        SalesTable.mcrOpenSalesOrder();

        salesTable_ds.active();
        element.mcrSetLineControls();
        SalesTable_ds.refresh();
    }

    if (enableOrderCompletion)
    {
        element.mcrSwapCompleteAndModifyButtons();
    }
}

