using Microsoft.Dynamics.Platform.Integration.Office;

[Form]
public class ReturnTableListPage extends FormRun implements OfficeIGeneratedWorkbookCustomExporter, OfficeIMenuCustomizer, OfficeITemplateCustomExporter
{
    private const str ExportReturnOrderHeaderAndLinesToExcelOptionId = 'ReturnOrderHeaderAndLinesCustom';

}

/// <summary>
/// Customizes the options used to populate the Office Menu with the return order entities.
/// </summary>
/// <param name = "_menuOptions">The menu options to be customized.</param>
public void customizeMenuOptions(OfficeMenuOptions _menuOptions)
{
    ListIterator dataEntityOptionsIterator = new ListIterator(_menuOptions.dataEntityOptions());

    while (dataEntityOptionsIterator.more())
    {
        OfficeMenuDataEntityOptions dataEntityOptions = dataEntityOptionsIterator.value();

        DictDataEntity dataEntity = new DictDataEntity(tablename2Id(dataEntityOptions.dataEntityName()));

        if (dataEntity.formRef() != menuItemDisplayStr(ReturnTableDetails))
        {
            dataEntityOptionsIterator.delete();
        }
        else
        {
            dataEntityOptionsIterator.next();
        }
    }

    List exportOptions = _menuOptions.customMenuItems();

    var exportOption = OfficeGeneratedExportMenuItem::construct(dataentityviewstr(ReturnOrderLineV2Entity), ExportReturnOrderHeaderAndLinesToExcelOptionId);
    exportOption.displayName(strfmt("@SYS76681", "@SCM:ReturnOrderHeaderAndLines", '', curExt()));
    exportOptions.addEnd(exportOption);
}

public ExportToExcelDataEntityContext getDataEntityContext(OfficeGeneratedExportMenuItem _menuItem)
{
    ExportToExcelDataEntityContext context = null;

    switch (_menuItem.id())
    {
        case ExportReturnOrderHeaderAndLinesToExcelOptionId:
            context = ExportToExcelDataEntityContext::constructDefault(dataentityviewstr(ReturnOrderHeaderV2Entity));

            var returnOrderHeaderEntityInfo = context.findEntityByName(dataentityviewstr(ReturnOrderHeaderV2Entity));

            if (returnOrderHeaderEntityInfo)
            {
                var returnOrderHeaderFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(ReturnOrderHeaderV2Entity));
                var returnOrderHeaderIdFilter = returnOrderHeaderFilterBuilder.areEqual(fieldStr(ReturnOrderHeaderV2Entity, ReturnOrderNumber), SalesTable.SalesId);

                returnOrderHeaderEntityInfo.filter(returnOrderHeaderFilterBuilder.and(returnOrderHeaderFilterBuilder.companyFilter(), returnOrderHeaderIdFilter));
            }

            var returnOrderLineEntityInfo = context.addEntityDefault(dataentityviewstr(ReturnOrderLineV2Entity));
            var returnOrderLineFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(ReturnOrderLineV2Entity));
            var returnOrderLineIdFilter = returnOrderLineFilterBuilder.areEqual(fieldStr(ReturnOrderLineV2Entity, ReturnOrderNumber), SalesTable.SalesId);
            returnOrderLineEntityInfo.filter(returnOrderLineFilterBuilder.and(returnOrderLineFilterBuilder.companyFilter(), returnOrderLineIdFilter));
            break;
    }

    return context;
}

public Map getInitialTemplateFilters(OfficeTemplateExportMenuItem _menuItem)
{
    Map filtersToApply = new Map(Types::String, Types::Class);

    if (_menuItem.dataEntityName() == dataentityviewstr(ReturnOrderHeaderEntity))
    {
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(ReturnOrderLineEntity));

        filtersToApply.insert(dataentityviewstr(ReturnOrderLineEntity), lineFilterBuilder.areEqual(fieldStr(ReturnOrderLineEntity, ReturnOrderNumber), SalesTable.SalesId));
    }

    if (_menuItem.dataEntityName() == dataentityviewstr(ReturnOrderHeaderV2Entity))
    {
        ExportToExcelFilterTreeBuilder lineFilterBuilder = new ExportToExcelFilterTreeBuilder(dataentityviewstr(ReturnOrderLineV2Entity));

        filtersToApply.insert(dataentityviewstr(ReturnOrderLineV2Entity), lineFilterBuilder.areEqual(fieldStr(ReturnOrderLineV2Entity, ReturnOrderNumber), SalesTable.SalesId));
    }

    return filtersToApply;
}

public void updateTemplateSettings(OfficeTemplateExportMenuItem _menuItem, Microsoft.Dynamics.Platform.Integration.Office.SettingsManager _settingsManager)
{
    // No implementation details for this since we don't need to update the template settings
}

/// <summary>
/// Handles ReturnCreateReplacement button click event.
/// </summary>
void clicked()
{
    SalesTableForm_Return salesTableForm;

    super();
    salesTableForm = SalesTableForm::construct(SalesTableFormId::ReturnOrder, element.args().record());
    salesTableForm.parmFormDataSource(salesTable_ds);
    salesTableForm.parmArgs(element.args());
    salesTableForm.returnCreateReplacement();
}

