/// <summary>
/// Enable the buttons in the engineering change group, if an engineering change case category exists.
/// </summary>
private void enableEngineeringChangeButtons()
{
    boolean caseCategoryFound = CaseCategoryHierarchyDetail::existsByCategoryType(CaseCategoryType::EngineeringChange);

    engineeringChangeCreateCase.enabled(caseCategoryFound);
    engineeringChangeAssociateCase.enabled(caseCategoryFound);
    engineeringChangeAddToLog.enabled(caseCategoryFound);
}

void init()
{
    configurationKeyConfigEnabled = isConfigurationkeyEnabled(configurationKeyNum(config));

    super();

    this.enableEngineeringChangeButtons();
}

public void initActivityDataSource(FormDataSource _formDataSource)
{
    SysDictTable sysDictTable = new SysDictTable(_formDataSource.table());

    if (!isConfigurationkeyEnabled(sysDictTable.configurationKeyId()))
    {
        _formDataSource.linkType(FormLinkType::Passive);
    }
}

RouteId routeId()
{
    RouteTable routeTable;

    if (element.args().dataset() == tableNum(RouteTable))
    {
        routeTable = element.args().record();
        return routeTable.RouteId;
    }
    else
    {
        return '';
    }
}

void run()
{
    super();

    ctrlRoute_OprNum.setFocus();
}

void setConfigId()
{
    if (configurationKeyConfigEnabled)
    {
        routeOpr_ConfigId.enabled(
            routeOpr.ItemCode == TableGroupAll::Table                   &&
            InventTable::find(routeOpr.ItemRelation).configActive());
    }
}

public void setCostingResource(WrkCtrId _wrkCtrId)
{
    if (routeOpr.RecId)
    {
        routeOpr.WrkCtrIdCost = _wrkCtrId;
        routeOpr.modifiedFieldValue(fieldStr(RouteOpr, WrkCtrIdCost));
    }
}

void setCtrl()
{
    routeOpr_ItemRel.enabled(routeOpr.ItemCode != TableGroupAll::All);
    routeOpr_RouteRel.enabled(routeOpr.RouteCode != RouteAll::All);
}

