public void linkActive()
{
    super();

    if (element.args().record() && element.args().dataset() == tablenum(RouteTable))
    {
        // Format the caption to include the RouteId as the TaskDouble pattern does not support it by default
        RouteTable routeTable = element.args().record();
        element.design().caption(strfmt("@SYS191501", "@SYS117969", routeTable.RouteId));
    }
}

