    routeOpr.RouteOprMap::postValidate();

    return ret;
}

public void enableFormulaFactor()
{
    routeOpr_ds.object(fieldNum(RouteOpr,FormulaFactor1)).allowEdit(routeOpr.RouteOprMap::enableFactorField());
}

public void executeQuery()
{
    str q;
    DictEnum de = new DictEnum(enumNum(RouteAll));

    if (route.RouteId)
        q = '('+
             '('+
              '('+fieldStr(RouteOpr,RouteCode)+' == '+de.name()+'::'+de.value2Symbol(RouteAll::Route)+') && '+
              '('+fieldStr(RouteOpr,RouteRelation)+' == "'+queryValue(route.RouteId)+'")'+
             ') || '+
              '('+fieldStr(RouteOpr,RouteCode)+' == '+de.name()+'::'+de.value2Symbol(RouteAll::All)+')'+
            ')';

    criteriaRouteRelation.value(q);

    super();
}

void initValue()
{
    super();

    routeOpr.RouteCode      = RouteAll::Route;
    routeOpr.RouteRelation  = route.RouteId;
}

