void modified()
{
    super();

    if (routeOpr.ItemCode   != lastItemCode ||
        routeOpr.ItemCode   == TableGroupAll::All
       )
    {
        routeOpr.ItemRelation = '';
        routeOpr.ConfigId     = '';
    }

    routeOpr_ItemRel.enabled(routeOpr.ItemCode != TableGroupAll::All);
    element.setConfigId();

    lastItemCode = routeOpr.ItemCode;
}

public void lookup(FormControl _formControl, str _filterStr)
{
    if (routeOpr.ItemCode == TableGroupAll::Table)
    {
        InventLookupItemIdByDefaultOrder::newLookupCtrl(_formControl, true).run();
    }
    else
    {
        super(_formControl, _filterStr);
    }
}

void modified()
{
    super();

    element.setConfigId();
}

void modified()
{
    super();

    if (routeOpr.RouteCode  != lastRouteCode ||
        routeOpr.RouteCode  == RouteAll::All
       )
    {
        routeOpr.RouteRelation = '';
    }

    routeOpr_RouteRel.enabled(routeOpr.RouteCode != RouteAll::All);

    lastRouteCode = routeOpr.RouteCode;
}

