boolean canEditDeleteRouteOpr()
{
    RouteOpr routeOpr = routeInventForm.findRouteOpr(prodRoute);

    return  (routeOpr.ItemCode    == TableGroupAll::Table   &&
             routeOpr.RouteCode   == RouteAll::Route        &&
            (configId             == routeOpr.ConfigId || configAll)) ? true : false;
}

boolean canModifyResourceRequirement()
{
    if (this.canEditDeleteRouteOpr())
    {
        RouteOpr routeOpr = routeInventForm.findRouteOpr(prodRoute);
        return routeOpr.canEdit();
    }

    return false;
}

