public Name findConfigName(
    EcoResConfigurationName _configurationName,
    LanguageId              _languageId = SystemParameters::getSystemLanguageId())
{
    EcoResProductMasterConfiguration    ecoResProductMasterConfiguration;
    EcoResConfiguration                 ecoResConfiguration;
    Name                                configurationDescription;

    ecoResConfiguration = EcoResConfiguration::findByName(_configurationName);
    ecoResProductMasterConfiguration = EcoResProductMasterConfiguration::find(
        callerInventTable.Product,
