/// <summary>
/// Get the list of <c>RouteTable</c> records from the selected <c>RouteVersion</c> records.
/// </summary>
/// <returns>
/// A list of <c>RouteTable</c> records
/// </returns>
List getRouteTableRecords()
{
    List            selectedRecords = new List(Types::Record);
    RouteVersion    routeVersionSelected;
    RouteTable      routeTable;

    // Get the RouteTable records for each selected RouteVersion
    routeVersionSelected = RouteVersion_ds.getFirst(true);
    if (!routeVersionSelected)
    {
        routeVersionSelected = RouteVersion_ds.cursor();
    }

    while (routeVersionSelected)
    {
        routeTable = routeVersionSelected.routeTable();
        selectedRecords.addEnd(routeTable);
        routeVersionSelected = RouteVersion_ds.getNext();
    }

    return selectedRecords;
}

