protected InventTable inventTable()
{
    switch(element.args().dataset())
    {
        case tableNum(InventTable):
            return element.args().record();

        case tableNum(InventDimCombination):
            InventDimCombination inventDimCombination = element.args().record();
            return inventDimCombination.inventTable();
    }
    return null;
}

void init()
{
    if (element.args())
    {
        callerInventTable = this.inventTable();
    }

    if (!callerInventTable)
    {
        throw error(Error::missingFormActiveBuffer(formstr(RouteInventProd), tablestr(InventTable)));
    }

    configAll = true;

    if (!callerInventTable.isFormulaOrBOMAllowed())
    {
        throw error(strfmt("@PRO2616", callerInventTable.ItemId));
    }

    if (!callerInventTable.inventItemType().canHaveRoutes())
    {
        throw error(strFmt("@SYS22874", callerInventTable.ItemId));
    }

    configurationKeyConfig = isConfigurationkeyEnabled(configurationKeyNum(config));

    routeInventForm = RouteInventForm::newForm(element);

    super();

    RouteUpdate.dataSource(formDataSourceStr(RouteInventProd, RouteVersion));

    if (RouteParameters::find().MandatoryDate)
    {
        routeVersion_FromDate.mandatory(true);
        routeVersion_ToDate.mandatory(true);
    }

    this.enableEngineeringChangeButtons();

    element.updateDesign(InventDimFormDesignUpdate::Init, callerInventTable);
}

