void setPrevOprNum(
    ProdRoute       _prodRoute,
    ProdRoute       _oldRoute,
    boolean         _append = false
    )
{
    ProdRoute   testRoute;

    if (_append)
    {
        _prodRoute.OprNum = _oldRoute.OprNum + RouteParameters::defaultOprNumInterval();

        return;
    }

    if (_oldRoute.ProdId)
    {
        testRoute.setTmp();
        testRoute.setTmpData(_prodRoute);

        select reverse firstonly testRoute
            order by ProdId,OprNum,OprPriority
            where testRoute.ProdId        == _oldRoute.ProdId        &&
                  (
                   (testRoute.OprNum        == _oldRoute.OprNum      &&
                    testRoute.OprPriority   <  _oldRoute.OprPriority
                   )                                                ||

                   testRoute.OprNum         <  _oldRoute.OprNum
                  );

        if (testRoute)
        {
            if (testRoute.OprNum < _oldRoute.OprNum)
                _prodRoute.OprNum = _oldRoute.OprNum - (_oldRoute.OprNum - testRoute.OprNum) div 2;
            else
                _prodRoute.OprNum = _oldRoute.OprNum;
        }
        else
            _prodRoute.OprNum = _oldRoute.OprNum - RouteParameters::defaultOprNumInterval();
    }
    else
        _prodRoute.OprNum = RouteParameters::defaultOprNumInterval();
}

