public boolean unpack(container packedClass)
{
    Integer     version      = conPeek(packedClass,1);

    switch (version)
    {
        case #CurrentVersion    :
            [version,#CurrentList] = packedClass;
            break;
        default :
            return false;
    }

    return true;
}

void updateDesign(InventDimFormDesignUpdate _mode, InventTable _inventTable)
{
    EcoResConfigurationName stdItemConfigurationName;
    InventDimParm           inventDimParm;

    switch (_mode)
    {
        case InventDimFormDesignUpdate::Init        :

            this.inventDimSetupObject();
            if (configurationKeyConfig)
            {
                isItemConfigActive = _inventTable.configActive();

                if (isItemConfigActive)
                {
                    stdItemConfigurationName = _inventTable.StandardConfigId;
                    configId = stdItemConfigurationName;

                    ctrlConfigAll.value(isItemConfigActive);
                    ctrlConfigId.text(stdItemConfigurationName);
                }
                else
                {
                    configAll = NoYes::Yes;
                    configId  = '';
                    ctrlGroupConfig.visible(false);
                }
            }

        case InventDimFormDesignUpdate::Active,
             InventDimFormDesignUpdate::LinkActive,
             InventDimFormDesignUpdate::FieldChange :

            inventDimParm.initDimBOMRouteVersion(InventDimGroupSetup::newInventTable(_inventTable));
            inventDimFormSetupRouteVersion.parmDimParmVisibleGrid(inventDimParm);
            inventDimFormSetupRouteVersion.parmDimParmEnabled(inventDimParm);
            inventDimFormSetupRouteVersion.formSetControls(true);
            break;

        default :
            throw error(strFmt("@SYS54195",funcName()));
    }
}

private void modifyFilterBasedOnConfiguration()
{
    boolean enableFilter       = (configId == '' || routeInventForm.parmIsConfigFilterApplied());
    boolean isStandardConfigId = configId == callerInventTable.StandardConfigId;

    ctrlConfigAll.allowEdit(isStandardConfigId ? true : enableFilter);
    ctrlConfigId.allowEdit(isStandardConfigId  ? true : enableFilter);
}

void updateRoute(RouteId routenum)
{
    RouteUpdate         routeUpdateBuffer;

    if (! routenum)
        return;

    if (! RouteTable::find(routenum).CheckRoute)
        return;

    routeUpdateBuffer  = RouteUpdate::newRouteId(routenum);

    try
    {
        routeUpdateBuffer.runOperation();
    }
    catch (Exception::Error)
    {
        exceptionTextFallThrough();
    }
}

