void linkActive()
{
    InventDim       inventDim;

    if (element.args().dataset() == tableNum(InventDimCombination))
    {
        InventDimCombination inventDimCombination = element.args().record();
        inventDim = inventDimCombination.inventDim();
    }

    inventDim.InventSiteId  = callerInventTable.inventInventSiteId('', inventDim.inventDimId);
    inventDim               = InventDim::findOrCreate(inventDim);

    currentActiveVersion = RouteVersion::findActive(callerInventTable.ItemId,
                                        DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()),
                                        callerInventTable.inventStandardQty(inventDim.InventDimId),
                                        inventDim);

    super();

    if (currentActiveVersion)
    {
        routeVersion_ds.positionToRecord(currentActiveVersion);
    }

    element.updateDesign(InventDimFormDesignUpdate::LinkActive, callerInventTable);

    element.setRouteVersionApprove();
}

int active()
{
    int ret;

    if (isItemConfigActive)
    {
        configId  = InventDimRouteVersion.configId != '' ? InventDimRouteVersion.configId : callerInventTable.StandardConfigId;
        configAll = (configId == '');

        ctrlConfigAll.value(configAll);
        ctrlConfigId.text(configId);

        element.modifyFilterBasedOnConfiguration();
    }

    if (lastUpdateRouteId && lastUpdateRouteId != routeVersion.RouteId)
        element.updateRoute(lastUpdateRouteId);

    ret = super();

    lastUpdateRouteId = routeVersion.RouteId;
    element.setRouteVersionApprove();
    element.enableFields();

    return ret;
}

