public void executeQuery()
{
    RouteOpr    routeOpr = routeInventForm.findRouteOpr(prodRoute);

    Query                   query = new Query();
    QueryBuildDataSource    qbs;
    qbs = query.addDataSource(tableNum(DocuRef));
    qbs.name(docuRef_ds.name());

    qbs.addRange(fieldNum(DocuRef,RefCompanyId)).value(queryValue(routeOpr.company()));
    qbs.addRange(fieldNum(DocuRef,RefTableId)).value(queryValue(routeOpr.TableId));
    qbs.addRange(fieldNum(DocuRef,RefRecId)).value(queryValue(routeOpr.RecId));

    docuRef_ds.query(query);

    super();
}

