public void write()
{
    WrkCtrRequirementQuantity   quantity = wrkCtrActivityRequirementSet.Quantity;
    WrkCtrLoadPct               loadPct  = wrkCtrActivityRequirementSet.LoadPercent;

    if (element.canEditDeleteRouteOpr())
    {
        super();
    }
    else
    {
        //Change data of the set back to the original values
        wrkCtrActivityRequirementSet.data(wrkCtrActivityRequirementSet.orig());

        //Force creation of new item specific RouteOpr and assign quantity and load percentage to the new set
        routeInventForm.prodRouteDSWritePost(false, true);

        wrkCtrRouteOprActivity_ds.executeQuery();
        wrkCtrActivity_ds.executeQuery();
        wrkCtrActivityRequirementSet_ds.executeQuery();

        wrkCtrActivityRequirementSet.Quantity = quantity;
        wrkCtrActivityRequirementSet.LoadPercent = loadPct;
        wrkCtrActivityRequirementSet.write();
    }
}

public void delete()
{
    if (wrkCtrActivityRequirementSet.RecId)
    {
        //Deletion should be performed on the primary datasource
        prodRoute_ds.delete();
        wrkCtrActivityRequirementSet_ds.executeQuery();
    }
}

