public void modified()
{
    RouteOprTimeProcess newProcessTime;

    newProcessTime = prodRoute.ProcessTime * lastWrkCtrRequirementQty / (wrkCtrActivityRequirementSet.Quantity ? wrkCtrActivityRequirementSet.Quantity : 1);
    lastWrkCtrRequirementQty = wrkCtrActivityRequirementSet.Quantity;

    if (newProcessTime != prodRoute.ProcessTime)
    {
        if (Box::yesNo(strFmt("@SYS23350", prodRoute.ProcessTime, newProcessTime), DialogButton::Yes) == DialogButton::Yes)
        {
            prodRoute.ProcessTime= newProcessTime;
        }
    }

    super();
}

public void executeQuery()
{
    RouteOpr    routeOpr = routeInventForm.findRouteOpr(prodRoute);
    QueryBuildDataSource    queryBuildDataSource;

    queryBuildRangeRouteOpr.value(queryValue(routeOpr.RecId));
    queryBuildRangeRouteOprDataAreaId.value(queryValue(routeOpr.DataAreaId));

    queryBuildDataSource                = this.query().dataSourceTable(tableNum(WrkCtrRouteOprActivity));

    super();
}

public void init()
{
    QueryBuildDataSource    queryBuildDataSource;

    element.initActivityDataSource(this);

    super();

    queryBuildDataSource                = this.query().dataSourceTable(tableNum(WrkCtrRouteOprActivity));

    queryBuildRangeRouteOpr             = queryBuildDataSource.addRange(fieldNum(WrkCtrRouteOprActivity,RouteOpr));
    queryBuildRangeRouteOprDataAreaId   = queryBuildDataSource.addRange(fieldNum(WrkCtrRouteOprActivity,RouteOprDataAreaId));
}

public void init()
{
    element.initActivityDataSource(this);

    super();
}

void clicked()
{
    RouteId         fromRouteId;
    InventSiteId    fromSiteId;
    InventDim       inventDim;
    RouteId         newRouteId;
    RouteTable      routeTable;
    super();

    if (routeVersion)
    {
        fromRouteId = routeVersion.RouteId;
        fromSiteId  = inventDimRouteVersion.InventSiteId;
    }

    gridRoute.setFocus();

    [newRouteId, inventDim.InventSiteId] = RouteTable::promptCreateRoute(callerInventTable.ItemId,configId,fromRouteId,fromSiteId);

    if (newRouteId)
    {
        if (RouteBOMInventProdRemoveFiltersFlight::instance().isEnabled())
        {
            routeVersion_ds.removeFilter();
        }

        routeVersion_ds.create();
        inventDim = InventDim::findOrCreate(inventDim);
        routeVersion.InventDimId = inventDim.InventDimId;

        inventDimRouteVersion.data(inventDim);
        inventDimRouteVersion_ds.setCurrent();

        routeTable = RouteTable::find(newRouteId);

        routeVersion.initFromRouteTable(routeTable);
        routeVersion.Name   = routeTable.Name;
        routeVersion_ds.write();
        routeVersion_ds.refresh();
    }

    element.setRouteVersionApprove();
}

