/// <summary>
///     Event method that occurs when the control has its value modified.
/// </summary>
/// <returns>
///     true if the modification of value was successful in the control.
/// </returns>
public boolean modified()
{
    boolean ret = super();

    if (ret)
    {
        configAll = this.value();

        if (ctrlConfigId.allowEdit() == configAll)
        {
            // if the allow edit status needs to change
            ctrlConfigId.allowEdit(configAll == NoYes::No);
        }

        ctrlConfigId.modified(true);
        prodRoute_ds.executeQuery();
    }

    return ret;
}

void lookup()
{
    ConfigLookup::lookupConfigId(this, routeVersion.ItemId, false);
}

/// <summary>
///     Event method that occurs when the control has its value modified.
/// </summary>
/// <param name="_displayValueUpdateOnly">
///     A boolean value indicating whether the update should avoid reselecting the form records.
/// </param>
/// <returns>
///     true if the modification of value was successful in the control.
/// </returns>
public boolean modified(boolean _displayValueUpdateOnly = false)
{
    Name    selectedValue;
    boolean ret = super();

    if (ret)
    {
        selectedValue = this.text();
        if (configAll)
        {
            configId = '';
        }
        else if (_displayValueUpdateOnly || (configId != selectedValue))
        {
            if (_displayValueUpdateOnly && !selectedValue)
            {
                this.text(configIdLastSelected);
                selectedValue = configIdLastSelected;
            }

            configId = selectedValue;
            if (!configId)
            {
                //no ID is selected, reset the All flag.
                ctrlConfigAll.value(NoYes::Yes);
                configAll = NoYes::Yes;
                this.allowEdit(false);
                ctrlConfigName.text('');
            }
            else
            {
                configIdLastSelected = configId;
                ctrlConfigName.text(element.findConfigName(selectedValue));
            }

            if (!_displayValueUpdateOnly)
            {
                routeInventForm.parmIsConfigFilterApplied(true);
                prodRoute_DS.executeQuery();
            }
        }
    }

    return ret;
}

public void pageActivated()
{
    ProdRoute_DS.write();
    super();
}

public void pageActivated()
{
    boolean allow;

    super();

    allow = element.canEditDeleteRouteOpr();
    docuRef_ds.allowCreate(allow);
    docuRef_ds.allowEdit(allow);
    docuRef_ds.allowDelete(allow);
    docuRef_ds.executeQuery();
}
