void init()
{
    if (element.args().caller())
    {
        switch (element.args().dataset())
        {
            case tableNum(Route), tableNum(RouteOprTable):
                if (fieldName2id(element.args().dataset(), fieldStr(RouteOpr,OprId)))
                {
                    linkOpr = true;
                }
                break;
        }
    }

    super();

    if (linkOpr)
    {
        Identification_OprId.visible(false);
    }
}

