void setItemRelation(TableGroupAll _oldSel, TableGroupAll _newSel)
{
    if (_newSel != _oldSel || _newSel == TableGroupAll::All)
    {
        routeOpr.ItemRelation = '';
        routeOpr.ConfigId     = '';
    }

    identification_ItemRelation.enabled(_newSel != TableGroupAll::All);
    identification_ConfigId.enabled(_newSel == TableGroupAll::Table && InventTable::find(routeOpr.ItemRelation).configActive());
}

void setRouteRelation(RouteAll _oldSel, RouteAll _newSel)
{
    if (_newSel != _oldSel || _newSel == RouteAll::All)
    {
        routeOpr.RouteRelation = '';
    }

    identification_RouteRelation.enabled(_newSel != RouteAll::All);
}

