public void selectionChanged()
{
    super();

    identification_ItemRelation.enabled(routeOpr.ItemCode   != TableGroupAll::All);
    identification_ConfigId.enabled(routeOpr.ItemCode == TableGroupAll::Table
                              && InventTable::find(routeOpr.ItemRelation).configActive());
    identification_RouteRelation.enabled(routeOpr.RouteCode != RouteAll::All);

    routeOpr_ds.enableFormulaFactor();
}

